  `postcode` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `telephone` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `fax` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `misc` mediumtext COLLATE utf8mb4_unicode_ci,
  `image` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `email_to` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `default_con` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `published` tinyint(1) NOT NULL DEFAULT '0',
  `checked_out` int(10) unsigned NOT NULL DEFAULT '0',
  `checked_out_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `ordering` int(11) NOT NULL DEFAULT '0',
  `params` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_id` int(11) NOT NULL DEFAULT '0',
  `catid` int(11) NOT NULL DEFAULT '0',
  `access` int(10) unsigned NOT NULL DEFAULT '0',
  `mobile` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `webpage` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `sortname1` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `sortname2` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `sortname3` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `language` varchar(7) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `created_by` int(10) unsigned NOT NULL DEFAULT '0',
  `created_by_alias` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `modified_by` int(10) unsigned NOT NULL DEFAULT '0',
  `metakey` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `metadesc` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `metadata` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `featured` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT 'Set if contact is featured.',
  `xreference` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT 'A reference to enable linkages to external data sets.',
  `publish_up` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `publish_down` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `version` int(10) unsigned NOT NULL DEFAULT '1',
  `hits` int(10) unsigned NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- --------------------------------------------------------

--
-- Структура таблицы `qhk3j_content`
--

CREATE TABLE IF NOT EXISTS `qhk3j_content` (
  `id` int(10) unsigned NOT NULL,
  `asset_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'FK to the #__assets table.',
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `alias` varchar(400) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL DEFAULT '',
  `introtext` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `fulltext` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `state` tinyint(3) NOT NULL DEFAULT '0',
  `catid` int(10) unsigned NOT NULL DEFAULT '0',
  `created` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `created_by` int(10) unsigned NOT NULL DEFAULT '0',
  `created_by_alias` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `modified_by` int(10) unsigned NOT NULL DEFAULT '0',
  `checked_out` int(10) unsigned NOT NULL DEFAULT '0',
  `checked_out_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `publish_up` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `publish_down` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `images` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `urls` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `attribs` varchar(5120) COLLATE utf8mb4_unicode_ci NOT NULL,
  `version` int(10) unsigned NOT NULL DEFAULT '1',
  `ordering` int(11) NOT NULL DEFAULT '0',
  `metakey` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `metadesc` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `access` int(10) unsigned NOT NULL DEFAULT '0',
  `hits` int(10) unsigned NOT NULL DEFAULT '0',
  `metadata` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `featured` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT 'Set if article is featured.',
  `language` char(7) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'The language code for the article.',
  `xreference` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT 'A reference to enable linkages to external data sets.'
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Дамп данных таблицы `qhk3j_content`
--

INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(1, 63, 'Создание материалов в Joomla', 'sozdanie-materialov-v-joomla', '<div class="page-header">&nbsp;</div>\r\n<div>\r\n<p>Данный урок полностью будет посвящен одной из основных тем при создании любого сайта – материалам Joomla. Научимся создавать и редактировать материалы, а так же познакомимся с основными настройками материалов.</p>\r\n<h2>Что такое материалы Joomla</h2>\r\n<p>Материалами в Joomla называют статьи, которые в дальнейшем будут отображаться в предназначенной для них области контента на сайте. Другими словами материалы это текстовая составляющая любого сайта. Материалы можно группировать по различным параметрам и относить к тому либо иному разделу (категории), о разделах и категориях в Joomla мы говорили&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">в предыдущем уроке посвященному структуре сайта</a>. А теперь давайте попробуем создать первый материал для нашего сайта.</p>\r\n<h2>Как создать материал в Joomla</h2>\r\n', '\r\n<p>Для работы с материалами в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство">админке</a>&nbsp;Джумлы есть специальный раздел, который так и называется «Менеджер материалов». Чтобы в него попасть заходим в панель управления сайтом http://имя_сайта/administrator, проходим авторизацию и наблюдаем следующее:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/1/1.jpg" title="Панель управления (админка) сайта на Joomla 3"><img alt="Панель управления (админка) сайта на Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/1/1-fit-690x489.jpg" /></a></p>\r\n<p>Теперь нам надо перейти в тот самый раздел «Менеджер материалов», для этого в верхнем меню панели управления жмем на пункт «Материалы» и в выпадающем меню жмем «Менеджер материалов».</p>\r\n<p><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/1.jpg" title="Для создания материала в Joomla переходим в соответствующий пункт меню"><img alt="Для создания материала в Joomla переходим в соответствующий пункт меню" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/1-fit-690x241.jpg" /></a></p>\r\n<p>После этих действий мы попадаем на страницу «Менеджер материалов: Материалы», выглядит она следующим образом:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/2.jpg" title="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla"><img alt="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/2-fit-690x192.jpg" /></a></p>\r\n<p><strong>Для создания материала имеется одноименная кнопка «Создать»</strong>, кстати, именно такая кнопка служит для создания чего угодно в Joomla, будь то материал,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Категории и структура сайта">категория</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/custom-fields" title="Дополнительные поля в Joomla 3">поле</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">меню</a>&nbsp;и т.д. В общем, жмем кнопку «Создать» и попадаем на страницу создания материала, которая выглядит примерно так:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/3.jpg" title="Страница создания материала в Joomla 3"><img alt="Страница создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/3-fit-690x360.jpg" /></a></p>\r\n<p>На скриншоте выше я обозначил цифрами важные поля, которые понадобятся для создания материала, давайте их рассмотрим.</p>\r\n<ul>\r\n<li>Поля под цифрами 1 – 3, а так же 5 и 6 мы рассматривать не будем, так как мы уже с ними познакомились на уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">созданию категорий</a>, если подзабыли, то можете перечитать их назначение там.</li>\r\n<li>Поле №4 –&nbsp;<strong>«Категория»</strong>&nbsp;служит для указания, к какой категории будет отнесен данный материал. Именно с его помощью структурируются статьи на сайте. Выбираем ту категорию, которая нам нужна, в моем случае выбрана категория по умолчанию.</li>\r\n<li>Поле №7 –&nbsp;<strong>«Избранные»</strong>&nbsp;это переключатель с двумя положениями «Да» и «Нет», если установлено значение «Да» то материал будет считаться избранным и наоборот. Что такое избранные материалы? Это те же самые материалы (возможно, те из них, которые Вы посчитали важными), в дальнейшем их модно будет выводить в виде блога при помощи специального типа пункта меню. В следующих уроках мы разберем избранные материалы подробнее.</li>\r\n<li>Поле №8 –&nbsp;<strong>«Метки»</strong>&nbsp;с их помощью можно объединять различные материалы по типу. К примеру, у вас автомобильный сайт на котором представлены различные марки автомобилей. К каждой марке можно дописать различные метки, например – грузовой, легковой, гусеничный автомобиль, полноприводный и так далее, а в дальнейшем можно выводить на страницах сайта только те автомобили в настройках материала которой присутствует та или иная метка. Меток у одного материала может быть больше одной.</li>\r\n<li>Поле №9 –&nbsp;<strong>«Комментарий версии»</strong>&nbsp;тут все просто, по желанию при создании либо редактировании материала мы можем написать небольшой комментарий, что было сделано или изменено, а в дальнейшем при неудачном сохранении сможем откатиться к интересующей нас версии материала. Иногда это удобно, но я этим никогда не пользовался.</li>\r\n</ul>\r\n<p>Ну и самое главное это поле, в котором будет содержаться сам текст материала. Писать можно что угодно, главное по теме. Чуть выше поля расположены кнопки редактирования текста, все стандартно, чем то напоминает текстовый редактор MS Word из далекого прошлого. Встроенный текстовый редактор в Joomla 3 не особо удобен (по крайней мере, для меня), в дальнейшем я покажу, как его заменить более привлекательным и функциональным.</p>\r\n<p>Пишем основной текст и заполняем все необходимые поля, на этом процесс создания материала в Joomla можно и завершить. Однако мы рассмотрели далеко не все возможные настройки, связанные с материалом, думаю, стоит их рассмотреть сразу.</p>\r\n<p>Еще раз посмотрите на страницу создания материала, сразу под заголовком можно увидеть несколько вкладок, мы рассмотрели только одну из них, которая в данный момент активна, эта вкладка «Материал». Но кроме неё есть еще 5 вкладок – Изображения и ссылки, Отображение, Публикация, Параметры редактирования и Права доступа. Давайте разберем каждую из них.</p>\r\n<h2>Дополнительные параметры материала</h2>\r\n<h3>Изображения и ссылки</h3>\r\n<p>Жмем на вторую вкладку&nbsp;<strong>«Изображения и ссылки»</strong>&nbsp;и видим нечто подобное:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/4.jpg" title="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3"><img alt="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/4-fit-690x352.jpg" /></a></p>\r\n<p>Все настройки на данной вкладке можно разделить на три области (смотрите скриншот выше) – миниатюра изображения, основное изображение и ссылки. Разберем их подробнее:</p>\r\n<ul>\r\n<li><strong>Миниатюра изображения</strong>&nbsp;– другими словами изображение для вступительного текста, данное изображение можно увидеть, когда статьи выводятся в виде блога. Тут необходимо выбрать само изображение (на хостинге), указать способ его выравнивания (по умолчанию слева). Кроме того можно указать альтернативный текст который будет отображен в том случае если изображение, по каким-то причинам не может быть отображено (пользователь отключил показ изображений) и написать (по желанию) его заголовок, текст заголовка будет отображаться под изображением.</li>\r\n<li><strong>Основное изображение</strong>&nbsp;– изображение полного текста материала. В плане настроек тут все аналогично изображению вступительного текста, но выводится данное изображение уже не в блоге, а в тексте самой статьи в верхней части.</li>\r\n<li><strong>Ссылки&nbsp;</strong>– если честно сомнительный блок, не понятно, для каких целей они были предусмотрены разработчиками Joomla, но они все же есть. Тут можно указать прямую ссылку на другую страницу сайта, или вообще на другой сайт, указать её текст, а так же способ её открытия (в новом окне, в том же окне или модально). По умолчанию ссылки выводятся в самом верху страницы, перед основным текстом статьи.</li>\r\n</ul>\r\n<h3>Отображение</h3>\r\n<p>Идем дальше, а дальше у нас вкладка&nbsp;<strong>«Отображение»</strong>, жмем на неё и наблюдаем следующую картину:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/5.jpg" title="Вкладка «Отображение» на странице создания материала в Joomla 3"><img alt="Вкладка «Отображение» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/5-fit-690x353.jpg" /></a></p>\r\n<p>Данная вкладка (скриншот выше) служит для настройки отображения статьи. Тут можно запретить или разрешить отображение категории, к которой относится материал, автора, даты создания, изменения и публикации, количество просмотров и рейтинг статьи, и многое другое. Все возможные настройки у меня не влезли в скриншот, да это и не требуется. Вы самостоятельно можете поиграться с ними и посмотреть, что за что отвечает, расписывать все по пунктам не вижу смысла.</p>\r\n<p>Хочу обратить внимание, что если для каждого материала вы используете определенные настройки, которые каждый раз повторяются, то изменять их рекомендуется не тут, а на странице&nbsp;<strong>«Настройки менеджера материалов»</strong>&nbsp;(на вкладке «Материалы»). Попасть на страницу «Настройки менеджера материалов» можно нажав на кнопку «Настройки» в правом верхнем углу менеджера материалов:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/6.jpg" title="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;"><img alt="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/6-fit-690x179.jpg" /></a></p>\r\n<p>А вот так выглядит сама страница&nbsp;<strong>«Настройки менеджера материалов»</strong>:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/7.jpg" title="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3"><img alt="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/7-fit-690x357.jpg" /></a></p>\r\n<h3>Публикация</h3>\r\n<p>Переходим к следующей вкладке –&nbsp;<strong>«Публикация»</strong>&nbsp;и смотрим, что она из себя представляет:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/8.jpg" title="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/8-fit-690x357.jpg" /></a></p>\r\n<p>На вкладке «<strong>Публикация</strong>» мы можем редактировать дату создания и начала публикации статьи, а так же можем указать дату и время, когда статья будет снята с публикации. Но все эти поля не столь важны, важными полями для нас (и не только для нас) являются поля с пометкой мета-тег. Давайте их рассмотрим подробнее:</p>\r\n<ul>\r\n<li><strong>Мета-тег Description</strong>&nbsp;- используется поисковыми роботами для показа описания страницы в результатах поиска, другими словами это краткое описание статьи, которое желательно уместить в 120 - 150 символов</li>\r\n<li><strong>Мета-тег Keywords</strong>&nbsp;– в данное поле вводим через запятую ключевые слова, по которым пользователи будут находить данную страницу. Сразу хочу оговориться, что не стоит писать те ключевые слова, которые не относятся к данному материалу или к странице вообще, от этого будет только хуже.</li>\r\n<li><strong>Мета-тег Robots</strong>&nbsp;– с помощью данного параметра мы можем указать, как должен действовать поисковый робот, посетивший данную страницу. Вариантов всего четыре:\r\n<ul>\r\n<li><em><strong>Index, Follow</strong></em>&nbsp;– разрешает роботу индексировать страницу и переходить по ссылкам</li>\r\n<li><em><strong>No index, follow</strong></em>&nbsp;– запрещает роботу индексировать страницу, но разрешает переход по ссылкам</li>\r\n<li><em><strong>Index, No follow</strong></em>&nbsp;– индексация разрешена, переход по ссылкам запрещен</li>\r\n<li><em><strong>No index, no follow</strong></em>&nbsp;– запрет индексации и перехода по ссылкам</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Хоть в настоящее время и бытует мнение, что&nbsp;<strong>поля Мета-тег Description и Мета-тег Keywords утратили свою значимость</strong>&nbsp;и не учитываются поисковыми роботами, но я все же рекомендую не оставлять их без внимания.</p>\r\n<h3>Параметры редактирования</h3>\r\n<p>Следующая вкладка&nbsp;<strong>«Параметры редактирования»</strong>, давайте на неё нажмем и посмотрим, что в ней содержится.</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/9.jpg" title="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/9-fit-690x243.jpg" /></a></p>\r\n<p>Содержание данной вкладки не большое, состоит всего из четырех параметров –&nbsp;<strong>Показывать параметры публикации</strong>,&nbsp;<strong>Показывать параметры материала</strong>,&nbsp;<strong>Изображения и ссылки (панель управления)</strong>&nbsp;и&nbsp;<strong>Изображения и ссылки (сайт)</strong>. Что это такое? Ничего особенного, с помощью данных полей можно скрыть отображение, а соответственно и запретить вносить изменения в упомянутые выше вкладки. По умолчанию доступ разрешен, давайте попробуем в выпадающих списках выбрать значение «Нет» и посмотрим, что у нас получится (предварительно надо нажать на кнопку «Сохранить»). Результат я показал на скриншоте ниже:</p>\r\n<p><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/10.jpg" title="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками"><img alt="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/10-fit-690x290.jpg" /></a></p>\r\n<p>Не знаю, для каких целей это может потребоваться, возможно, на тот случай если Вы сами себе не доверяете, либо если потребуется скрыть данные поля от других пользователей. В общем, редактировать либо нет значения на данной вкладке дело сугубо личное.</p>\r\n<h3>Права доступа</h3>\r\n<p>Последняя вкладка&nbsp;<strong>«Права доступа»</strong>, на ней можно так сказать разделять и властвовать – разрешать или запрещать определенным категориям пользователей определенные действия со статьей. Таких действий всего три – удалить, изменить и изменить состояние. На начальном этапе советую ничего тут не трогать.</p>\r\n<p>Вот и подошел к концу урок по созданию материала Joomla, осталось только его сохранить. Для сохранения как вы уже наверно догадались, есть специальная кнопка «Сохранить» в верхней части страницы. Жмем на неё и видим сообщение об успешном сохранении, после этого можно закрыть материал, нажав на соответствующую кнопку. У меня получилось примерно так:</p>\r\n<p><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/11.jpg" title="Пример готового материала в Joomla 3.7"><img alt="Пример готового материала в Joomla 3.7" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/11-fit-690x359.jpg" /></a></p>\r\n<h2>Несколько советов правильного оформления материалов</h2>\r\n<p>Казалось бы, что тут сложного, написал статьи, сохранил и готово. Это конечно так, но я все же советую подойти к этому процессу со всей ответственность, чтобы в дальнейшем не пришлось изменять ранее написанные тексты.</p>\r\n<ul>\r\n<li><strong>Пишите статью предварительно в текстовом редакторе</strong>, например в MS Word. Это поможет избежать возможных ошибок в тексте, даже если Вы невероятно грамотный человек и никогда не допускаете ошибок в написании, это будет не лишним, от опечаток никто не застрахован.</li>\r\n<li>После того как статья написана&nbsp;<strong>пользуйтесь специальной вставкой</strong>, которая убирает форматирование текста. Например, в редакторе JCK есть специальная кнопка «Вставить только текст» (можно просто скопировать текст из Word в блокнот, и только потом вставлять его в материал). Таким образом, Вы избавитесь от лишнего html мусора.</li>\r\n<li><strong>Не стоит переусердствовать при оформлении текста</strong>&nbsp;материала в редакторе Joomla – делать его слишком красивым, ярким, использовать различные шрифты и прочее. Это все лишнее, Весь текст должен оформляться при помощи файла стилей, а в самом материале стоит выделить только заголовки.</li>\r\n<li><strong>Заголовки в теле статьи не должны быть первого уровня</strong>&nbsp;(первый уровень это название материала).</li>\r\n<li><strong>Текст обязательно должен быть уникальным</strong>, это положительно скажется позиции сайта в поисковой выдаче.</li>\r\n</ul>\r\n<p>На этом данный урок я завершаю, а в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">следующем уроке поговорим о меню</a>, именно с его помощью мы будем выводить материалы на страницы сайта.</p>\r\n<p>Пока не забыл! Для тех, кому интересно, что это за текстовый редактор был показан на последнем скриншоте, отвечаю - это&nbsp;<a href="http://joom4all.ru/sites-creation/basics/components" title="Разбираем что такое компоненты Joomla на примере редактора JCK">JCK Editor</a>.</p>\r\n</div>', -2, 14, '2017-10-24 17:46:34', 38, '', '2017-10-24 19:23:05', 38, 0, '0000-00-00 00:00:00', '2017-10-24 17:46:34', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":"http:\\/\\/joom4all.ru\\/sites-creation\\/basics\\/creating-articles","urlatext":"\\u041e\\u0442\\u043a\\u0443\\u0434\\u0430 \\u043c\\u0430\\u0442\\u0435\\u0440\\u0438\\u0430\\u043b","targeta":"1","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 11, 0, '', '', 1, 8, '{"robots":"","author":"","rights":"","xreference":""}', 1, '*', ''),
(2, 75, 'Установка JCE редактора для Joomla 3', 'razbiraem-chto-takoe-komponenty-joomla-na-primere-redaktora-jck', '<div>\r\n<p>Инструкция по установке и основных отличительных качеств редактора JCE для Joomla в отличии от стандартного встроенного редактора TinyMCE.</p>\r\n', '<ol>\r\n<li>Переходим по&nbsp;<a style="color: #0088cc;" href="http://joomla.ru/downloads/extensions/jce" rel="nofollow" target="_blank">ссылке</a>&nbsp;и скачиваем русскую версию редактора JCE.</li>\r\n<li>В админке вашего сайта заходим в меню&nbsp;<strong>Расширения</strong>&nbsp;-&gt;&nbsp;<strong>Менеджер расширений</strong>&nbsp;и переходим на вкладку "<strong>Загрузить файл пакета</strong>"</li>\r\n<li>Жмем кнопку "<strong>Выберите файл</strong>", указываете файл архива который вы скачали в первом пункте и жмем кнопку&nbsp;<strong>Загрузить и установить</strong>.<img style="display: block; margin-left: auto; margin-right: auto;" src="http://myplugin.ru/images/2014/10/07/jce1.png" alt="jce1" width="500" height="284" /></li>\r\n<li>После установки переходим в меню&nbsp;<strong>Система</strong>&nbsp;-&gt;&nbsp;<strong>Общие настройки</strong>&nbsp;и находим пункт&nbsp;<strong>Редактор по умолчанию.<img style="display: block; margin-left: auto; margin-right: auto;" src="http://myplugin.ru/images/2014/10/07/jce2.png" alt="jce2" width="500" height="420" /></strong></li>\r\n<li>В пункте&nbsp;<strong>Редактор по умолчанию</strong>&nbsp;выбираем из выпадающего списка "<strong>Редактор JCE</strong>" или "<strong>Editor JCE</strong>". Жмем кнопку&nbsp;<strong>Сохранить.</strong></li>\r\n<li>Переходим в менеджер материалом и создаем новый материал, наслаждаясь новым редактором.</li>\r\n</ol>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://myplugin.ru/images/2014/10/07/jce3.png" alt="jce3" width="500" height="378" /></p>\r\n<p>Самой классной особенностью на мой взгляд, считается то, что с помощью этого редактора можно загружать картинки на сайт, удалять картинки. Так же можно создавать, удалять, переименовывать папки, что не было возможно в редакторе TinyMCE.</p>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://myplugin.ru/images/2014/10/07/jce4.png" alt="jce4" width="500" height="432" /></p>\r\n<p><img style="display: block; margin-left: auto; margin-right: auto;" src="http://myplugin.ru/images/2014/10/07/jce5.png" alt="jce5" width="500" height="374" /></p>\r\n<p>В редакторе&nbsp;<strong>TinyMCE</strong>&nbsp;есть возможность только загружать картинки на сайт, а вот если приходится создать папку или удалить ошибочно загруженную фотографию, то приходится обращаться к медиа-менеджеру через пункт меню&nbsp;<strong>Материалы</strong>&nbsp;-&gt;&nbsp;<strong>Медиа-Менеджер</strong>.</p>\r\n</div>', 1, 14, '2017-10-24 18:51:01', 38, '', '2017-10-24 19:22:01', 38, 0, '0000-00-00 00:00:00', '2017-10-24 18:51:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 8, '', '', 1, 3, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(3, 77, 'Создание материалов в Joomla', 'sozdanie-materialov-v-joomla-2', '<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данный урок полностью будет посвящен одной из основных тем при создании любого сайта – материалам Joomla. Научимся создавать и редактировать материалы, а так же познакомимся с основными настройками материалов.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Что такое материалы Joomla</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Материалами в Joomla называют статьи, которые в дальнейшем будут отображаться в предназначенной для них области контента на сайте. Другими словами материалы это текстовая составляющая любого сайта. Материалы можно группировать по различным параметрам и относить к тому либо иному разделу (категории), о разделах и категориях в Joomla мы говорили&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">в предыдущем уроке посвященному структуре сайта</a>. А теперь давайте попробуем создать первый материал для нашего сайта.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Как создать материал в Joomla</h2>\r\n', '\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Для работы с материалами в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство">админке</a>&nbsp;Джумлы есть специальный раздел, который так и называется «Менеджер материалов». Чтобы в него попасть заходим в панель управления сайтом http://имя_сайта/administrator, проходим авторизацию и наблюдаем следующее:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/1/1.jpg" title="Панель управления (админка) сайта на Joomla 3"><img alt="Панель управления (админка) сайта на Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/1/1-fit-690x489.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Теперь нам надо перейти в тот самый раздел «Менеджер материалов», для этого в верхнем меню панели управления жмем на пункт «Материалы» и в выпадающем меню жмем «Менеджер материалов».</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/1.jpg" title="Для создания материала в Joomla переходим в соответствующий пункт меню"><img alt="Для создания материала в Joomla переходим в соответствующий пункт меню" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/1-fit-690x241.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">После этих действий мы попадаем на страницу «Менеджер материалов: Материалы», выглядит она следующим образом:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/2.jpg" title="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla"><img alt="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/2-fit-690x192.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><strong>Для создания материала имеется одноименная кнопка «Создать»</strong>, кстати, именно такая кнопка служит для создания чего угодно в Joomla, будь то материал,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Категории и структура сайта">категория</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/custom-fields" title="Дополнительные поля в Joomla 3">поле</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">меню</a>&nbsp;и т.д. В общем, жмем кнопку «Создать» и попадаем на страницу создания материала, которая выглядит примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/3.jpg" title="Страница создания материала в Joomla 3"><img alt="Страница создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/3-fit-690x360.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На скриншоте выше я обозначил цифрами важные поля, которые понадобятся для создания материала, давайте их рассмотрим.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li>Поля под цифрами 1 – 3, а так же 5 и 6 мы рассматривать не будем, так как мы уже с ними познакомились на уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">созданию категорий</a>, если подзабыли, то можете перечитать их назначение там.</li>\r\n<li>Поле №4 –&nbsp;<strong>«Категория»</strong>&nbsp;служит для указания, к какой категории будет отнесен данный материал. Именно с его помощью структурируются статьи на сайте. Выбираем ту категорию, которая нам нужна, в моем случае выбрана категория по умолчанию.</li>\r\n<li>Поле №7 –&nbsp;<strong>«Избранные»</strong>&nbsp;это переключатель с двумя положениями «Да» и «Нет», если установлено значение «Да» то материал будет считаться избранным и наоборот. Что такое избранные материалы? Это те же самые материалы (возможно, те из них, которые Вы посчитали важными), в дальнейшем их модно будет выводить в виде блога при помощи специального типа пункта меню. В следующих уроках мы разберем избранные материалы подробнее.</li>\r\n<li>Поле №8 –&nbsp;<strong>«Метки»</strong>&nbsp;с их помощью можно объединять различные материалы по типу. К примеру, у вас автомобильный сайт на котором представлены различные марки автомобилей. К каждой марке можно дописать различные метки, например – грузовой, легковой, гусеничный автомобиль, полноприводный и так далее, а в дальнейшем можно выводить на страницах сайта только те автомобили в настройках материала которой присутствует та или иная метка. Меток у одного материала может быть больше одной.</li>\r\n<li>Поле №9 –&nbsp;<strong>«Комментарий версии»</strong>&nbsp;тут все просто, по желанию при создании либо редактировании материала мы можем написать небольшой комментарий, что было сделано или изменено, а в дальнейшем при неудачном сохранении сможем откатиться к интересующей нас версии материала. Иногда это удобно, но я этим никогда не пользовался.</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Ну и самое главное это поле, в котором будет содержаться сам текст материала. Писать можно что угодно, главное по теме. Чуть выше поля расположены кнопки редактирования текста, все стандартно, чем то напоминает текстовый редактор MS Word из далекого прошлого. Встроенный текстовый редактор в Joomla 3 не особо удобен (по крайней мере, для меня), в дальнейшем я покажу, как его заменить более привлекательным и функциональным.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Пишем основной текст и заполняем все необходимые поля, на этом процесс создания материала в Joomla можно и завершить. Однако мы рассмотрели далеко не все возможные настройки, связанные с материалом, думаю, стоит их рассмотреть сразу.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Еще раз посмотрите на страницу создания материала, сразу под заголовком можно увидеть несколько вкладок, мы рассмотрели только одну из них, которая в данный момент активна, эта вкладка «Материал». Но кроме неё есть еще 5 вкладок – Изображения и ссылки, Отображение, Публикация, Параметры редактирования и Права доступа. Давайте разберем каждую из них.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Дополнительные параметры материала</h2>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Изображения и ссылки</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Жмем на вторую вкладку&nbsp;<strong>«Изображения и ссылки»</strong>&nbsp;и видим нечто подобное:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/4.jpg" title="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3"><img alt="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/4-fit-690x352.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Все настройки на данной вкладке можно разделить на три области (смотрите скриншот выше) – миниатюра изображения, основное изображение и ссылки. Разберем их подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Миниатюра изображения</strong>&nbsp;– другими словами изображение для вступительного текста, данное изображение можно увидеть, когда статьи выводятся в виде блога. Тут необходимо выбрать само изображение (на хостинге), указать способ его выравнивания (по умолчанию слева). Кроме того можно указать альтернативный текст который будет отображен в том случае если изображение, по каким-то причинам не может быть отображено (пользователь отключил показ изображений) и написать (по желанию) его заголовок, текст заголовка будет отображаться под изображением.</li>\r\n<li><strong>Основное изображение</strong>&nbsp;– изображение полного текста материала. В плане настроек тут все аналогично изображению вступительного текста, но выводится данное изображение уже не в блоге, а в тексте самой статьи в верхней части.</li>\r\n<li><strong>Ссылки&nbsp;</strong>– если честно сомнительный блок, не понятно, для каких целей они были предусмотрены разработчиками Joomla, но они все же есть. Тут можно указать прямую ссылку на другую страницу сайта, или вообще на другой сайт, указать её текст, а так же способ её открытия (в новом окне, в том же окне или модально). По умолчанию ссылки выводятся в самом верху страницы, перед основным текстом статьи.</li>\r\n</ul>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Отображение</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Идем дальше, а дальше у нас вкладка&nbsp;<strong>«Отображение»</strong>, жмем на неё и наблюдаем следующую картину:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/5.jpg" title="Вкладка «Отображение» на странице создания материала в Joomla 3"><img alt="Вкладка «Отображение» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/5-fit-690x353.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данная вкладка (скриншот выше) служит для настройки отображения статьи. Тут можно запретить или разрешить отображение категории, к которой относится материал, автора, даты создания, изменения и публикации, количество просмотров и рейтинг статьи, и многое другое. Все возможные настройки у меня не влезли в скриншот, да это и не требуется. Вы самостоятельно можете поиграться с ними и посмотреть, что за что отвечает, расписывать все по пунктам не вижу смысла.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хочу обратить внимание, что если для каждого материала вы используете определенные настройки, которые каждый раз повторяются, то изменять их рекомендуется не тут, а на странице&nbsp;<strong>«Настройки менеджера материалов»</strong>&nbsp;(на вкладке «Материалы»). Попасть на страницу «Настройки менеджера материалов» можно нажав на кнопку «Настройки» в правом верхнем углу менеджера материалов:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/6.jpg" title="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;"><img alt="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/6-fit-690x179.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">А вот так выглядит сама страница&nbsp;<strong>«Настройки менеджера материалов»</strong>:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/7.jpg" title="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3"><img alt="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/7-fit-690x357.jpg" /></a></p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Публикация</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Переходим к следующей вкладке –&nbsp;<strong>«Публикация»</strong>&nbsp;и смотрим, что она из себя представляет:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/8.jpg" title="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/8-fit-690x357.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На вкладке «<strong>Публикация</strong>» мы можем редактировать дату создания и начала публикации статьи, а так же можем указать дату и время, когда статья будет снята с публикации. Но все эти поля не столь важны, важными полями для нас (и не только для нас) являются поля с пометкой мета-тег. Давайте их рассмотрим подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Мета-тег Description</strong>&nbsp;- используется поисковыми роботами для показа описания страницы в результатах поиска, другими словами это краткое описание статьи, которое желательно уместить в 120 - 150 символов</li>\r\n<li><strong>Мета-тег Keywords</strong>&nbsp;– в данное поле вводим через запятую ключевые слова, по которым пользователи будут находить данную страницу. Сразу хочу оговориться, что не стоит писать те ключевые слова, которые не относятся к данному материалу или к странице вообще, от этого будет только хуже.</li>\r\n<li><strong>Мета-тег Robots</strong>&nbsp;– с помощью данного параметра мы можем указать, как должен действовать поисковый робот, посетивший данную страницу. Вариантов всего четыре:\r\n<ul>\r\n<li><em><strong>Index, Follow</strong></em>&nbsp;– разрешает роботу индексировать страницу и переходить по ссылкам</li>\r\n<li><em><strong>No index, follow</strong></em>&nbsp;– запрещает роботу индексировать страницу, но разрешает переход по ссылкам</li>\r\n<li><em><strong>Index, No follow</strong></em>&nbsp;– индексация разрешена, переход по ссылкам запрещен</li>\r\n<li><em><strong>No index, no follow</strong></em>&nbsp;– запрет индексации и перехода по ссылкам</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хоть в настоящее время и бытует мнение, что&nbsp;<strong>поля Мета-тег Description и Мета-тег Keywords утратили свою значимость</strong>&nbsp;и не учитываются поисковыми роботами, но я все же рекомендую не оставлять их без внимания.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Параметры редактирования</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Следующая вкладка&nbsp;<strong>«Параметры редактирования»</strong>, давайте на неё нажмем и посмотрим, что в ней содержится.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/9.jpg" title="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/9-fit-690x243.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Содержание данной вкладки не большое, состоит всего из четырех параметров –&nbsp;<strong>Показывать параметры публикации</strong>,&nbsp;<strong>Показывать параметры материала</strong>,&nbsp;<strong>Изображения и ссылки (панель управления)</strong>&nbsp;и&nbsp;<strong>Изображения и ссылки (сайт)</strong>. Что это такое? Ничего особенного, с помощью данных полей можно скрыть отображение, а соответственно и запретить вносить изменения в упомянутые выше вкладки. По умолчанию доступ разрешен, давайте попробуем в выпадающих списках выбрать значение «Нет» и посмотрим, что у нас получится (предварительно надо нажать на кнопку «Сохранить»). Результат я показал на скриншоте ниже:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/10.jpg" title="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками"><img alt="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/10-fit-690x290.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Не знаю, для каких целей это может потребоваться, возможно, на тот случай если Вы сами себе не доверяете, либо если потребуется скрыть данные поля от других пользователей. В общем, редактировать либо нет значения на данной вкладке дело сугубо личное.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Права доступа</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Последняя вкладка&nbsp;<strong>«Права доступа»</strong>, на ней можно так сказать разделять и властвовать – разрешать или запрещать определенным категориям пользователей определенные действия со статьей. Таких действий всего три – удалить, изменить и изменить состояние. На начальном этапе советую ничего тут не трогать.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Вот и подошел к концу урок по созданию материала Joomla, осталось только его сохранить. Для сохранения как вы уже наверно догадались, есть специальная кнопка «Сохранить» в верхней части страницы. Жмем на неё и видим сообщение об успешном сохранении, после этого можно закрыть материал, нажав на соответствующую кнопку. У меня получилось примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/11.jpg" title="Пример готового материала в Joomla 3.7"><img alt="Пример готового материала в Joomla 3.7" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/11-fit-690x359.jpg" /></a></p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Несколько советов правильного оформления материалов</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Казалось бы, что тут сложного, написал статьи, сохранил и готово. Это конечно так, но я все же советую подойти к этому процессу со всей ответственность, чтобы в дальнейшем не пришлось изменять ранее написанные тексты.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Пишите стаью предварительно в текстовом редакторе</strong>, например в MS Word. Это поможет избежать возможных ошибок в тексте, даже если Вы невероятно грамотный человек и никогда не допускаете ошибок в написании, это будет не лишним, от опечаток никто не застрахован.</li>\r\n<li>После того как статья написана&nbsp;<strong>пользуйтесь специальной вставкой</strong>, которая убирает форматирование текста. Например, в редакторе JCK есть специальная кнопка «Вставить только текст» (можно просто скопировать текст из Word в блокнот, и только потом вставлять его в материал). Таким образом, Вы избавитесь от лишнего html мусора.</li>\r\n<li><strong>Не стоит переусердствовать при оформлении текста</strong>&nbsp;материала в редакторе Joomla – делать его слишком красивым, ярким, использовать различные шрифты и прочее. Это все лишнее, Весь текст должен оформляться при помощи файла стилей, а в самом материале стоит выделить только заголовки.</li>\r\n<li><strong>Заголовки в теле статьи не должны быть первого уровня</strong>&nbsp;(первый уровень это название материала).</li>\r\n<li><strong>Текст обязательно должен быть уникальным</strong>, это положительно скажется позиции сайта в поисковой выдаче.</li>\r\n</ul>\r\n<p>На этом данный урок я завершаю, а в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">следующем уроке поговорим о меню</a>, именно с его помощью мы будем выводить материалы на страницы сайта.</p>', 1, 14, '2017-10-24 19:26:46', 38, '', '2017-10-24 19:28:55', 38, 0, '0000-00-00 00:00:00', '2017-10-24 19:26:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 7, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(4, 78, 'Создание материалов в Joomla (2)', 'sozdanie-materialov-v-joomla-3', '<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данный урок полностью будет посвящен одной из основных тем при создании любого сайта – материалам Joomla. Научимся создавать и редактировать материалы, а так же познакомимся с основными настройками материалов.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Что такое материалы Joomla</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Материалами в Joomla называют статьи, которые в дальнейшем будут отображаться в предназначенной для них области контента на сайте. Другими словами материалы это текстовая составляющая любого сайта. Материалы можно группировать по различным параметрам и относить к тому либо иному разделу (категории), о разделах и категориях в Joomla мы говорили&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">в предыдущем уроке посвященному структуре сайта</a>. А теперь давайте попробуем создать первый материал для нашего сайта.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Как создать материал в Joomla</h2>\r\n', '\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Для работы с материалами в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство">админке</a>&nbsp;Джумлы есть специальный раздел, который так и называется «Менеджер материалов». Чтобы в него попасть заходим в панель управления сайтом http://имя_сайта/administrator, проходим авторизацию и наблюдаем следующее:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/1/1.jpg" title="Панель управления (админка) сайта на Joomla 3"><img alt="Панель управления (админка) сайта на Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/1/1-fit-690x489.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Теперь нам надо перейти в тот самый раздел «Менеджер материалов», для этого в верхнем меню панели управления жмем на пункт «Материалы» и в выпадающем меню жмем «Менеджер материалов».</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/1.jpg" title="Для создания материала в Joomla переходим в соответствующий пункт меню"><img alt="Для создания материала в Joomla переходим в соответствующий пункт меню" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/1-fit-690x241.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">После этих действий мы попадаем на страницу «Менеджер материалов: Материалы», выглядит она следующим образом:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/2.jpg" title="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla"><img alt="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/2-fit-690x192.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><strong>Для создания материала имеется одноименная кнопка «Создать»</strong>, кстати, именно такая кнопка служит для создания чего угодно в Joomla, будь то материал,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Категории и структура сайта">категория</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/custom-fields" title="Дополнительные поля в Joomla 3">поле</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">меню</a>&nbsp;и т.д. В общем, жмем кнопку «Создать» и попадаем на страницу создания материала, которая выглядит примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/3.jpg" title="Страница создания материала в Joomla 3"><img alt="Страница создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/3-fit-690x360.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На скриншоте выше я обозначил цифрами важные поля, которые понадобятся для создания материала, давайте их рассмотрим.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li>Поля под цифрами 1 – 3, а так же 5 и 6 мы рассматривать не будем, так как мы уже с ними познакомились на уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">созданию категорий</a>, если подзабыли, то можете перечитать их назначение там.</li>\r\n<li>Поле №4 –&nbsp;<strong>«Категория»</strong>&nbsp;служит для указания, к какой категории будет отнесен данный материал. Именно с его помощью структурируются статьи на сайте. Выбираем ту категорию, которая нам нужна, в моем случае выбрана категория по умолчанию.</li>\r\n<li>Поле №7 –&nbsp;<strong>«Избранные»</strong>&nbsp;это переключатель с двумя положениями «Да» и «Нет», если установлено значение «Да» то материал будет считаться избранным и наоборот. Что такое избранные материалы? Это те же самые материалы (возможно, те из них, которые Вы посчитали важными), в дальнейшем их модно будет выводить в виде блога при помощи специального типа пункта меню. В следующих уроках мы разберем избранные материалы подробнее.</li>\r\n<li>Поле №8 –&nbsp;<strong>«Метки»</strong>&nbsp;с их помощью можно объединять различные материалы по типу. К примеру, у вас автомобильный сайт на котором представлены различные марки автомобилей. К каждой марке можно дописать различные метки, например – грузовой, легковой, гусеничный автомобиль, полноприводный и так далее, а в дальнейшем можно выводить на страницах сайта только те автомобили в настройках материала которой присутствует та или иная метка. Меток у одного материала может быть больше одной.</li>\r\n<li>Поле №9 –&nbsp;<strong>«Комментарий версии»</strong>&nbsp;тут все просто, по желанию при создании либо редактировании материала мы можем написать небольшой комментарий, что было сделано или изменено, а в дальнейшем при неудачном сохранении сможем откатиться к интересующей нас версии материала. Иногда это удобно, но я этим никогда не пользовался.</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Ну и самое главное это поле, в котором будет содержаться сам текст материала. Писать можно что угодно, главное по теме. Чуть выше поля расположены кнопки редактирования текста, все стандартно, чем то напоминает текстовый редактор MS Word из далекого прошлого. Встроенный текстовый редактор в Joomla 3 не особо удобен (по крайней мере, для меня), в дальнейшем я покажу, как его заменить более привлекательным и функциональным.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Пишем основной текст и заполняем все необходимые поля, на этом процесс создания материала в Joomla можно и завершить. Однако мы рассмотрели далеко не все возможные настройки, связанные с материалом, думаю, стоит их рассмотреть сразу.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Еще раз посмотрите на страницу создания материала, сразу под заголовком можно увидеть несколько вкладок, мы рассмотрели только одну из них, которая в данный момент активна, эта вкладка «Материал». Но кроме неё есть еще 5 вкладок – Изображения и ссылки, Отображение, Публикация, Параметры редактирования и Права доступа. Давайте разберем каждую из них.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Дополнительные параметры материала</h2>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Изображения и ссылки</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Жмем на вторую вкладку&nbsp;<strong>«Изображения и ссылки»</strong>&nbsp;и видим нечто подобное:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/4.jpg" title="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3"><img alt="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/4-fit-690x352.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Все настройки на данной вкладке можно разделить на три области (смотрите скриншот выше) – миниатюра изображения, основное изображение и ссылки. Разберем их подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Миниатюра изображения</strong>&nbsp;– другими словами изображение для вступительного текста, данное изображение можно увидеть, когда статьи выводятся в виде блога. Тут необходимо выбрать само изображение (на хостинге), указать способ его выравнивания (по умолчанию слева). Кроме того можно указать альтернативный текст который будет отображен в том случае если изображение, по каким-то причинам не может быть отображено (пользователь отключил показ изображений) и написать (по желанию) его заголовок, текст заголовка будет отображаться под изображением.</li>\r\n<li><strong>Основное изображение</strong>&nbsp;– изображение полного текста материала. В плане настроек тут все аналогично изображению вступительного текста, но выводится данное изображение уже не в блоге, а в тексте самой статьи в верхней части.</li>\r\n<li><strong>Ссылки&nbsp;</strong>– если честно сомнительный блок, не понятно, для каких целей они были предусмотрены разработчиками Joomla, но они все же есть. Тут можно указать прямую ссылку на другую страницу сайта, или вообще на другой сайт, указать её текст, а так же способ её открытия (в новом окне, в том же окне или модально). По умолчанию ссылки выводятся в самом верху страницы, перед основным текстом статьи.</li>\r\n</ul>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Отображение</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Идем дальше, а дальше у нас вкладка&nbsp;<strong>«Отображение»</strong>, жмем на неё и наблюдаем следующую картину:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/5.jpg" title="Вкладка «Отображение» на странице создания материала в Joomla 3"><img alt="Вкладка «Отображение» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/5-fit-690x353.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данная вкладка (скриншот выше) служит для настройки отображения статьи. Тут можно запретить или разрешить отображение категории, к которой относится материал, автора, даты создания, изменения и публикации, количество просмотров и рейтинг статьи, и многое другое. Все возможные настройки у меня не влезли в скриншот, да это и не требуется. Вы самостоятельно можете поиграться с ними и посмотреть, что за что отвечает, расписывать все по пунктам не вижу смысла.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хочу обратить внимание, что если для каждого материала вы используете определенные настройки, которые каждый раз повторяются, то изменять их рекомендуется не тут, а на странице&nbsp;<strong>«Настройки менеджера материалов»</strong>&nbsp;(на вкладке «Материалы»). Попасть на страницу «Настройки менеджера материалов» можно нажав на кнопку «Настройки» в правом верхнем углу менеджера материалов:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/6.jpg" title="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;"><img alt="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/6-fit-690x179.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">А вот так выглядит сама страница&nbsp;<strong>«Настройки менеджера материалов»</strong>:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/7.jpg" title="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3"><img alt="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/7-fit-690x357.jpg" /></a></p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Публикация</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Переходим к следующей вкладке –&nbsp;<strong>«Публикация»</strong>&nbsp;и смотрим, что она из себя представляет:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/8.jpg" title="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/8-fit-690x357.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На вкладке «<strong>Публикация</strong>» мы можем редактировать дату создания и начала публикации статьи, а так же можем указать дату и время, когда статья будет снята с публикации. Но все эти поля не столь важны, важными полями для нас (и не только для нас) являются поля с пометкой мета-тег. Давайте их рассмотрим подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Мета-тег Description</strong>&nbsp;- используется поисковыми роботами для показа описания страницы в результатах поиска, другими словами это краткое описание статьи, которое желательно уместить в 120 - 150 символов</li>\r\n<li><strong>Мета-тег Keywords</strong>&nbsp;– в данное поле вводим через запятую ключевые слова, по которым пользователи будут находить данную страницу. Сразу хочу оговориться, что не стоит писать те ключевые слова, которые не относятся к данному материалу или к странице вообще, от этого будет только хуже.</li>\r\n<li><strong>Мета-тег Robots</strong>&nbsp;– с помощью данного параметра мы можем указать, как должен действовать поисковый робот, посетивший данную страницу. Вариантов всего четыре:\r\n<ul>\r\n<li><em><strong>Index, Follow</strong></em>&nbsp;– разрешает роботу индексировать страницу и переходить по ссылкам</li>\r\n<li><em><strong>No index, follow</strong></em>&nbsp;– запрещает роботу индексировать страницу, но разрешает переход по ссылкам</li>\r\n<li><em><strong>Index, No follow</strong></em>&nbsp;– индексация разрешена, переход по ссылкам запрещен</li>\r\n<li><em><strong>No index, no follow</strong></em>&nbsp;– запрет индексации и перехода по ссылкам</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хоть в настоящее время и бытует мнение, что&nbsp;<strong>поля Мета-тег Description и Мета-тег Keywords утратили свою значимость</strong>&nbsp;и не учитываются поисковыми роботами, но я все же рекомендую не оставлять их без внимания.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Параметры редактирования</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Следующая вкладка&nbsp;<strong>«Параметры редактирования»</strong>, давайте на неё нажмем и посмотрим, что в ней содержится.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/9.jpg" title="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/9-fit-690x243.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Содержание данной вкладки не большое, состоит всего из четырех параметров –&nbsp;<strong>Показывать параметры публикации</strong>,&nbsp;<strong>Показывать параметры материала</strong>,&nbsp;<strong>Изображения и ссылки (панель управления)</strong>&nbsp;и&nbsp;<strong>Изображения и ссылки (сайт)</strong>. Что это такое? Ничего особенного, с помощью данных полей можно скрыть отображение, а соответственно и запретить вносить изменения в упомянутые выше вкладки. По умолчанию доступ разрешен, давайте попробуем в выпадающих списках выбрать значение «Нет» и посмотрим, что у нас получится (предварительно надо нажать на кнопку «Сохранить»). Результат я показал на скриншоте ниже:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/10.jpg" title="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками"><img alt="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/10-fit-690x290.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Не знаю, для каких целей это может потребоваться, возможно, на тот случай если Вы сами себе не доверяете, либо если потребуется скрыть данные поля от других пользователей. В общем, редактировать либо нет значения на данной вкладке дело сугубо личное.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Права доступа</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Последняя вкладка&nbsp;<strong>«Права доступа»</strong>, на ней можно так сказать разделять и властвовать – разрешать или запрещать определенным категориям пользователей определенные действия со статьей. Таких действий всего три – удалить, изменить и изменить состояние. На начальном этапе советую ничего тут не трогать.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Вот и подошел к концу урок по созданию материала Joomla, осталось только его сохранить. Для сохранения как вы уже наверно догадались, есть специальная кнопка «Сохранить» в верхней части страницы. Жмем на неё и видим сообщение об успешном сохранении, после этого можно закрыть материал, нажав на соответствующую кнопку. У меня получилось примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/11.jpg" title="Пример готового материала в Joomla 3.7"><img alt="Пример готового материала в Joomla 3.7" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/11-fit-690x359.jpg" /></a></p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Несколько советов правильного оформления материалов</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Казалось бы, что тут сложного, написал статьи, сохранил и готово. Это конечно так, но я все же советую подойти к этому процессу со всей ответственность, чтобы в дальнейшем не пришлось изменять ранее написанные тексты.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Пишите стаью предварительно в текстовом редакторе</strong>, например в MS Word. Это поможет избежать возможных ошибок в тексте, даже если Вы невероятно грамотный человек и никогда не допускаете ошибок в написании, это будет не лишним, от опечаток никто не застрахован.</li>\r\n<li>После того как статья написана&nbsp;<strong>пользуйтесь специальной вставкой</strong>, которая убирает форматирование текста. Например, в редакторе JCK есть специальная кнопка «Вставить только текст» (можно просто скопировать текст из Word в блокнот, и только потом вставлять его в материал). Таким образом, Вы избавитесь от лишнего html мусора.</li>\r\n<li><strong>Не стоит переусердствовать при оформлении текста</strong>&nbsp;материала в редакторе Joomla – делать его слишком красивым, ярким, использовать различные шрифты и прочее. Это все лишнее, Весь текст должен оформляться при помощи файла стилей, а в самом материале стоит выделить только заголовки.</li>\r\n<li><strong>Заголовки в теле статьи не должны быть первого уровня</strong>&nbsp;(первый уровень это название материала).</li>\r\n<li><strong>Текст обязательно должен быть уникальным</strong>, это положительно скажется позиции сайта в поисковой выдаче.</li>\r\n</ul>\r\n<p>На этом данный урок я завершаю, а в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">следующем уроке поговорим о меню</a>, именно с его помощью мы будем выводить материалы на страницы сайта.</p>', 1, 14, '2017-10-24 19:26:46', 38, '', '2017-10-24 19:37:50', 38, 0, '0000-00-00 00:00:00', '2017-10-24 19:26:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 6, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(5, 79, 'Создание материалов в Joomla (3)', 'sozdanie-materialov-v-joomla-4', '<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данный урок полностью будет посвящен одной из основных тем при создании любого сайта – материалам Joomla. Научимся создавать и редактировать материалы, а так же познакомимся с основными настройками материалов.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Что такое материалы Joomla</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Материалами в Joomla называют статьи, которые в дальнейшем будут отображаться в предназначенной для них области контента на сайте. Другими словами материалы это текстовая составляющая любого сайта. Материалы можно группировать по различным параметрам и относить к тому либо иному разделу (категории), о разделах и категориях в Joomla мы говорили&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">в предыдущем уроке посвященному структуре сайта</a>. А теперь давайте попробуем создать первый материал для нашего сайта.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Как создать материал в Joomla</h2>\r\n', '\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Для работы с материалами в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство">админке</a>&nbsp;Джумлы есть специальный раздел, который так и называется «Менеджер материалов». Чтобы в него попасть заходим в панель управления сайтом http://имя_сайта/administrator, проходим авторизацию и наблюдаем следующее:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/1/1.jpg" title="Панель управления (админка) сайта на Joomla 3"><img alt="Панель управления (админка) сайта на Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/1/1-fit-690x489.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Теперь нам надо перейти в тот самый раздел «Менеджер материалов», для этого в верхнем меню панели управления жмем на пункт «Материалы» и в выпадающем меню жмем «Менеджер материалов».</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/1.jpg" title="Для создания материала в Joomla переходим в соответствующий пункт меню"><img alt="Для создания материала в Joomla переходим в соответствующий пункт меню" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/1-fit-690x241.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">После этих действий мы попадаем на страницу «Менеджер материалов: Материалы», выглядит она следующим образом:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/2.jpg" title="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla"><img alt="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/2-fit-690x192.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><strong>Для создания материала имеется одноименная кнопка «Создать»</strong>, кстати, именно такая кнопка служит для создания чего угодно в Joomla, будь то материал,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Категории и структура сайта">категория</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/custom-fields" title="Дополнительные поля в Joomla 3">поле</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">меню</a>&nbsp;и т.д. В общем, жмем кнопку «Создать» и попадаем на страницу создания материала, которая выглядит примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/3.jpg" title="Страница создания материала в Joomla 3"><img alt="Страница создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/3-fit-690x360.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На скриншоте выше я обозначил цифрами важные поля, которые понадобятся для создания материала, давайте их рассмотрим.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li>Поля под цифрами 1 – 3, а так же 5 и 6 мы рассматривать не будем, так как мы уже с ними познакомились на уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">созданию категорий</a>, если подзабыли, то можете перечитать их назначение там.</li>\r\n<li>Поле №4 –&nbsp;<strong>«Категория»</strong>&nbsp;служит для указания, к какой категории будет отнесен данный материал. Именно с его помощью структурируются статьи на сайте. Выбираем ту категорию, которая нам нужна, в моем случае выбрана категория по умолчанию.</li>\r\n<li>Поле №7 –&nbsp;<strong>«Избранные»</strong>&nbsp;это переключатель с двумя положениями «Да» и «Нет», если установлено значение «Да» то материал будет считаться избранным и наоборот. Что такое избранные материалы? Это те же самые материалы (возможно, те из них, которые Вы посчитали важными), в дальнейшем их модно будет выводить в виде блога при помощи специального типа пункта меню. В следующих уроках мы разберем избранные материалы подробнее.</li>\r\n<li>Поле №8 –&nbsp;<strong>«Метки»</strong>&nbsp;с их помощью можно объединять различные материалы по типу. К примеру, у вас автомобильный сайт на котором представлены различные марки автомобилей. К каждой марке можно дописать различные метки, например – грузовой, легковой, гусеничный автомобиль, полноприводный и так далее, а в дальнейшем можно выводить на страницах сайта только те автомобили в настройках материала которой присутствует та или иная метка. Меток у одного материала может быть больше одной.</li>\r\n<li>Поле №9 –&nbsp;<strong>«Комментарий версии»</strong>&nbsp;тут все просто, по желанию при создании либо редактировании материала мы можем написать небольшой комментарий, что было сделано или изменено, а в дальнейшем при неудачном сохранении сможем откатиться к интересующей нас версии материала. Иногда это удобно, но я этим никогда не пользовался.</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Ну и самое главное это поле, в котором будет содержаться сам текст материала. Писать можно что угодно, главное по теме. Чуть выше поля расположены кнопки редактирования текста, все стандартно, чем то напоминает текстовый редактор MS Word из далекого прошлого. Встроенный текстовый редактор в Joomla 3 не особо удобен (по крайней мере, для меня), в дальнейшем я покажу, как его заменить более привлекательным и функциональным.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Пишем основной текст и заполняем все необходимые поля, на этом процесс создания материала в Joomla можно и завершить. Однако мы рассмотрели далеко не все возможные настройки, связанные с материалом, думаю, стоит их рассмотреть сразу.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Еще раз посмотрите на страницу создания материала, сразу под заголовком можно увидеть несколько вкладок, мы рассмотрели только одну из них, которая в данный момент активна, эта вкладка «Материал». Но кроме неё есть еще 5 вкладок – Изображения и ссылки, Отображение, Публикация, Параметры редактирования и Права доступа. Давайте разберем каждую из них.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Дополнительные параметры материала</h2>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Изображения и ссылки</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Жмем на вторую вкладку&nbsp;<strong>«Изображения и ссылки»</strong>&nbsp;и видим нечто подобное:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/4.jpg" title="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3"><img alt="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/4-fit-690x352.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Все настройки на данной вкладке можно разделить на три области (смотрите скриншот выше) – миниатюра изображения, основное изображение и ссылки. Разберем их подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Миниатюра изображения</strong>&nbsp;– другими словами изображение для вступительного текста, данное изображение можно увидеть, когда статьи выводятся в виде блога. Тут необходимо выбрать само изображение (на хостинге), указать способ его выравнивания (по умолчанию слева). Кроме того можно указать альтернативный текст который будет отображен в том случае если изображение, по каким-то причинам не может быть отображено (пользователь отключил показ изображений) и написать (по желанию) его заголовок, текст заголовка будет отображаться под изображением.</li>\r\n<li><strong>Основное изображение</strong>&nbsp;– изображение полного текста материала. В плане настроек тут все аналогично изображению вступительного текста, но выводится данное изображение уже не в блоге, а в тексте самой статьи в верхней части.</li>\r\n<li><strong>Ссылки&nbsp;</strong>– если честно сомнительный блок, не понятно, для каких целей они были предусмотрены разработчиками Joomla, но они все же есть. Тут можно указать прямую ссылку на другую страницу сайта, или вообще на другой сайт, указать её текст, а так же способ её открытия (в новом окне, в том же окне или модально). По умолчанию ссылки выводятся в самом верху страницы, перед основным текстом статьи.</li>\r\n</ul>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Отображение</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Идем дальше, а дальше у нас вкладка&nbsp;<strong>«Отображение»</strong>, жмем на неё и наблюдаем следующую картину:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/5.jpg" title="Вкладка «Отображение» на странице создания материала в Joomla 3"><img alt="Вкладка «Отображение» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/5-fit-690x353.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данная вкладка (скриншот выше) служит для настройки отображения статьи. Тут можно запретить или разрешить отображение категории, к которой относится материал, автора, даты создания, изменения и публикации, количество просмотров и рейтинг статьи, и многое другое. Все возможные настройки у меня не влезли в скриншот, да это и не требуется. Вы самостоятельно можете поиграться с ними и посмотреть, что за что отвечает, расписывать все по пунктам не вижу смысла.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хочу обратить внимание, что если для каждого материала вы используете определенные настройки, которые каждый раз повторяются, то изменять их рекомендуется не тут, а на странице&nbsp;<strong>«Настройки менеджера материалов»</strong>&nbsp;(на вкладке «Материалы»). Попасть на страницу «Настройки менеджера материалов» можно нажав на кнопку «Настройки» в правом верхнем углу менеджера материалов:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/6.jpg" title="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;"><img alt="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/6-fit-690x179.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">А вот так выглядит сама страница&nbsp;<strong>«Настройки менеджера материалов»</strong>:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/7.jpg" title="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3"><img alt="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/7-fit-690x357.jpg" /></a></p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Публикация</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Переходим к следующей вкладке –&nbsp;<strong>«Публикация»</strong>&nbsp;и смотрим, что она из себя представляет:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/8.jpg" title="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/8-fit-690x357.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На вкладке «<strong>Публикация</strong>» мы можем редактировать дату создания и начала публикации статьи, а так же можем указать дату и время, когда статья будет снята с публикации. Но все эти поля не столь важны, важными полями для нас (и не только для нас) являются поля с пометкой мета-тег. Давайте их рассмотрим подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Мета-тег Description</strong>&nbsp;- используется поисковыми роботами для показа описания страницы в результатах поиска, другими словами это краткое описание статьи, которое желательно уместить в 120 - 150 символов</li>\r\n<li><strong>Мета-тег Keywords</strong>&nbsp;– в данное поле вводим через запятую ключевые слова, по которым пользователи будут находить данную страницу. Сразу хочу оговориться, что не стоит писать те ключевые слова, которые не относятся к данному материалу или к странице вообще, от этого будет только хуже.</li>\r\n<li><strong>Мета-тег Robots</strong>&nbsp;– с помощью данного параметра мы можем указать, как должен действовать поисковый робот, посетивший данную страницу. Вариантов всего четыре:\r\n<ul>\r\n<li><em><strong>Index, Follow</strong></em>&nbsp;– разрешает роботу индексировать страницу и переходить по ссылкам</li>\r\n<li><em><strong>No index, follow</strong></em>&nbsp;– запрещает роботу индексировать страницу, но разрешает переход по ссылкам</li>\r\n<li><em><strong>Index, No follow</strong></em>&nbsp;– индексация разрешена, переход по ссылкам запрещен</li>\r\n<li><em><strong>No index, no follow</strong></em>&nbsp;– запрет индексации и перехода по ссылкам</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хоть в настоящее время и бытует мнение, что&nbsp;<strong>поля Мета-тег Description и Мета-тег Keywords утратили свою значимость</strong>&nbsp;и не учитываются поисковыми роботами, но я все же рекомендую не оставлять их без внимания.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Параметры редактирования</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Следующая вкладка&nbsp;<strong>«Параметры редактирования»</strong>, давайте на неё нажмем и посмотрим, что в ней содержится.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/9.jpg" title="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/9-fit-690x243.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Содержание данной вкладки не большое, состоит всего из четырех параметров –&nbsp;<strong>Показывать параметры публикации</strong>,&nbsp;<strong>Показывать параметры материала</strong>,&nbsp;<strong>Изображения и ссылки (панель управления)</strong>&nbsp;и&nbsp;<strong>Изображения и ссылки (сайт)</strong>. Что это такое? Ничего особенного, с помощью данных полей можно скрыть отображение, а соответственно и запретить вносить изменения в упомянутые выше вкладки. По умолчанию доступ разрешен, давайте попробуем в выпадающих списках выбрать значение «Нет» и посмотрим, что у нас получится (предварительно надо нажать на кнопку «Сохранить»). Результат я показал на скриншоте ниже:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/10.jpg" title="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками"><img alt="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/10-fit-690x290.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Не знаю, для каких целей это может потребоваться, возможно, на тот случай если Вы сами себе не доверяете, либо если потребуется скрыть данные поля от других пользователей. В общем, редактировать либо нет значения на данной вкладке дело сугубо личное.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Права доступа</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Последняя вкладка&nbsp;<strong>«Права доступа»</strong>, на ней можно так сказать разделять и властвовать – разрешать или запрещать определенным категориям пользователей определенные действия со статьей. Таких действий всего три – удалить, изменить и изменить состояние. На начальном этапе советую ничего тут не трогать.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Вот и подошел к концу урок по созданию материала Joomla, осталось только его сохранить. Для сохранения как вы уже наверно догадались, есть специальная кнопка «Сохранить» в верхней части страницы. Жмем на неё и видим сообщение об успешном сохранении, после этого можно закрыть материал, нажав на соответствующую кнопку. У меня получилось примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/11.jpg" title="Пример готового материала в Joomla 3.7"><img alt="Пример готового материала в Joomla 3.7" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/11-fit-690x359.jpg" /></a></p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Несколько советов правильного оформления материалов</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Казалось бы, что тут сложного, написал статьи, сохранил и готово. Это конечно так, но я все же советую подойти к этому процессу со всей ответственность, чтобы в дальнейшем не пришлось изменять ранее написанные тексты.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Пишите стаью предварительно в текстовом редакторе</strong>, например в MS Word. Это поможет избежать возможных ошибок в тексте, даже если Вы невероятно грамотный человек и никогда не допускаете ошибок в написании, это будет не лишним, от опечаток никто не застрахован.</li>\r\n<li>После того как статья написана&nbsp;<strong>пользуйтесь специальной вставкой</strong>, которая убирает форматирование текста. Например, в редакторе JCK есть специальная кнопка «Вставить только текст» (можно просто скопировать текст из Word в блокнот, и только потом вставлять его в материал). Таким образом, Вы избавитесь от лишнего html мусора.</li>\r\n<li><strong>Не стоит переусердствовать при оформлении текста</strong>&nbsp;материала в редакторе Joomla – делать его слишком красивым, ярким, использовать различные шрифты и прочее. Это все лишнее, Весь текст должен оформляться при помощи файла стилей, а в самом материале стоит выделить только заголовки.</li>\r\n<li><strong>Заголовки в теле статьи не должны быть первого уровня</strong>&nbsp;(первый уровень это название материала).</li>\r\n<li><strong>Текст обязательно должен быть уникальным</strong>, это положительно скажется позиции сайта в поисковой выдаче.</li>\r\n</ul>\r\n<p>На этом данный урок я завершаю, а в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">следующем уроке поговорим о меню</a>, именно с его помощью мы будем выводить материалы на страницы сайта.</p>', 1, 14, '2017-10-24 19:26:46', 38, '', '2017-10-24 19:38:10', 38, 0, '0000-00-00 00:00:00', '2017-10-24 19:26:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 5, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(6, 80, 'Создание материалов в Joomla (4)', 'sozdanie-materialov-v-joomla-5', '<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данный урок полностью будет посвящен одной из основных тем при создании любого сайта – материалам Joomla. Научимся создавать и редактировать материалы, а так же познакомимся с основными настройками материалов.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Что такое материалы Joomla</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Материалами в Joomla называют статьи, которые в дальнейшем будут отображаться в предназначенной для них области контента на сайте. Другими словами материалы это текстовая составляющая любого сайта. Материалы можно группировать по различным параметрам и относить к тому либо иному разделу (категории), о разделах и категориях в Joomla мы говорили&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">в предыдущем уроке посвященному структуре сайта</a>. А теперь давайте попробуем создать первый материал для нашего сайта.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Как создать материал в Joomla</h2>\r\n', '\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Для работы с материалами в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство">админке</a>&nbsp;Джумлы есть специальный раздел, который так и называется «Менеджер материалов». Чтобы в него попасть заходим в панель управления сайтом http://имя_сайта/administrator, проходим авторизацию и наблюдаем следующее:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/1/1.jpg" title="Панель управления (админка) сайта на Joomla 3"><img alt="Панель управления (админка) сайта на Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/1/1-fit-690x489.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Теперь нам надо перейти в тот самый раздел «Менеджер материалов», для этого в верхнем меню панели управления жмем на пункт «Материалы» и в выпадающем меню жмем «Менеджер материалов».</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/1.jpg" title="Для создания материала в Joomla переходим в соответствующий пункт меню"><img alt="Для создания материала в Joomla переходим в соответствующий пункт меню" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/1-fit-690x241.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">После этих действий мы попадаем на страницу «Менеджер материалов: Материалы», выглядит она следующим образом:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/2.jpg" title="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla"><img alt="Страница «Менеджер материалов: Материалы» - тут производятся все работы с материалами Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/2-fit-690x192.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><strong>Для создания материала имеется одноименная кнопка «Создать»</strong>, кстати, именно такая кнопка служит для создания чего угодно в Joomla, будь то материал,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Категории и структура сайта">категория</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/custom-fields" title="Дополнительные поля в Joomla 3">поле</a>,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">меню</a>&nbsp;и т.д. В общем, жмем кнопку «Создать» и попадаем на страницу создания материала, которая выглядит примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/3.jpg" title="Страница создания материала в Joomla 3"><img alt="Страница создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/3-fit-690x360.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На скриншоте выше я обозначил цифрами важные поля, которые понадобятся для создания материала, давайте их рассмотрим.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li>Поля под цифрами 1 – 3, а так же 5 и 6 мы рассматривать не будем, так как мы уже с ними познакомились на уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере">созданию категорий</a>, если подзабыли, то можете перечитать их назначение там.</li>\r\n<li>Поле №4 –&nbsp;<strong>«Категория»</strong>&nbsp;служит для указания, к какой категории будет отнесен данный материал. Именно с его помощью структурируются статьи на сайте. Выбираем ту категорию, которая нам нужна, в моем случае выбрана категория по умолчанию.</li>\r\n<li>Поле №7 –&nbsp;<strong>«Избранные»</strong>&nbsp;это переключатель с двумя положениями «Да» и «Нет», если установлено значение «Да» то материал будет считаться избранным и наоборот. Что такое избранные материалы? Это те же самые материалы (возможно, те из них, которые Вы посчитали важными), в дальнейшем их модно будет выводить в виде блога при помощи специального типа пункта меню. В следующих уроках мы разберем избранные материалы подробнее.</li>\r\n<li>Поле №8 –&nbsp;<strong>«Метки»</strong>&nbsp;с их помощью можно объединять различные материалы по типу. К примеру, у вас автомобильный сайт на котором представлены различные марки автомобилей. К каждой марке можно дописать различные метки, например – грузовой, легковой, гусеничный автомобиль, полноприводный и так далее, а в дальнейшем можно выводить на страницах сайта только те автомобили в настройках материала которой присутствует та или иная метка. Меток у одного материала может быть больше одной.</li>\r\n<li>Поле №9 –&nbsp;<strong>«Комментарий версии»</strong>&nbsp;тут все просто, по желанию при создании либо редактировании материала мы можем написать небольшой комментарий, что было сделано или изменено, а в дальнейшем при неудачном сохранении сможем откатиться к интересующей нас версии материала. Иногда это удобно, но я этим никогда не пользовался.</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Ну и самое главное это поле, в котором будет содержаться сам текст материала. Писать можно что угодно, главное по теме. Чуть выше поля расположены кнопки редактирования текста, все стандартно, чем то напоминает текстовый редактор MS Word из далекого прошлого. Встроенный текстовый редактор в Joomla 3 не особо удобен (по крайней мере, для меня), в дальнейшем я покажу, как его заменить более привлекательным и функциональным.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Пишем основной текст и заполняем все необходимые поля, на этом процесс создания материала в Joomla можно и завершить. Однако мы рассмотрели далеко не все возможные настройки, связанные с материалом, думаю, стоит их рассмотреть сразу.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Еще раз посмотрите на страницу создания материала, сразу под заголовком можно увидеть несколько вкладок, мы рассмотрели только одну из них, которая в данный момент активна, эта вкладка «Материал». Но кроме неё есть еще 5 вкладок – Изображения и ссылки, Отображение, Публикация, Параметры редактирования и Права доступа. Давайте разберем каждую из них.</p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Дополнительные параметры материала</h2>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Изображения и ссылки</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Жмем на вторую вкладку&nbsp;<strong>«Изображения и ссылки»</strong>&nbsp;и видим нечто подобное:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/4.jpg" title="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3"><img alt="Вкладка «Изображения и ссылки» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/4-fit-690x352.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Все настройки на данной вкладке можно разделить на три области (смотрите скриншот выше) – миниатюра изображения, основное изображение и ссылки. Разберем их подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Миниатюра изображения</strong>&nbsp;– другими словами изображение для вступительного текста, данное изображение можно увидеть, когда статьи выводятся в виде блога. Тут необходимо выбрать само изображение (на хостинге), указать способ его выравнивания (по умолчанию слева). Кроме того можно указать альтернативный текст который будет отображен в том случае если изображение, по каким-то причинам не может быть отображено (пользователь отключил показ изображений) и написать (по желанию) его заголовок, текст заголовка будет отображаться под изображением.</li>\r\n<li><strong>Основное изображение</strong>&nbsp;– изображение полного текста материала. В плане настроек тут все аналогично изображению вступительного текста, но выводится данное изображение уже не в блоге, а в тексте самой статьи в верхней части.</li>\r\n<li><strong>Ссылки&nbsp;</strong>– если честно сомнительный блок, не понятно, для каких целей они были предусмотрены разработчиками Joomla, но они все же есть. Тут можно указать прямую ссылку на другую страницу сайта, или вообще на другой сайт, указать её текст, а так же способ её открытия (в новом окне, в том же окне или модально). По умолчанию ссылки выводятся в самом верху страницы, перед основным текстом статьи.</li>\r\n</ul>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Отображение</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Идем дальше, а дальше у нас вкладка&nbsp;<strong>«Отображение»</strong>, жмем на неё и наблюдаем следующую картину:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/5.jpg" title="Вкладка «Отображение» на странице создания материала в Joomla 3"><img alt="Вкладка «Отображение» на странице создания материала в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/5-fit-690x353.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Данная вкладка (скриншот выше) служит для настройки отображения статьи. Тут можно запретить или разрешить отображение категории, к которой относится материал, автора, даты создания, изменения и публикации, количество просмотров и рейтинг статьи, и многое другое. Все возможные настройки у меня не влезли в скриншот, да это и не требуется. Вы самостоятельно можете поиграться с ними и посмотреть, что за что отвечает, расписывать все по пунктам не вижу смысла.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хочу обратить внимание, что если для каждого материала вы используете определенные настройки, которые каждый раз повторяются, то изменять их рекомендуется не тут, а на странице&nbsp;<strong>«Настройки менеджера материалов»</strong>&nbsp;(на вкладке «Материалы»). Попасть на страницу «Настройки менеджера материалов» можно нажав на кнопку «Настройки» в правом верхнем углу менеджера материалов:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/6.jpg" title="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;"><img alt="Для доступа к пакетной настройке материалов в менеджере материалов Joomla надо нажать на кнопку &quot;Настройка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/6-fit-690x179.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">А вот так выглядит сама страница&nbsp;<strong>«Настройки менеджера материалов»</strong>:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/7.jpg" title="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3"><img alt="Страница «Настройки менеджера материалов» служит для пакетной обработки настроек материалов Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/7-fit-690x357.jpg" /></a></p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Публикация</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Переходим к следующей вкладке –&nbsp;<strong>«Публикация»</strong>&nbsp;и смотрим, что она из себя представляет:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/8.jpg" title="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Публикация» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/8-fit-690x357.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">На вкладке «<strong>Публикация</strong>» мы можем редактировать дату создания и начала публикации статьи, а так же можем указать дату и время, когда статья будет снята с публикации. Но все эти поля не столь важны, важными полями для нас (и не только для нас) являются поля с пометкой мета-тег. Давайте их рассмотрим подробнее:</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Мета-тег Description</strong>&nbsp;- используется поисковыми роботами для показа описания страницы в результатах поиска, другими словами это краткое описание статьи, которое желательно уместить в 120 - 150 символов</li>\r\n<li><strong>Мета-тег Keywords</strong>&nbsp;– в данное поле вводим через запятую ключевые слова, по которым пользователи будут находить данную страницу. Сразу хочу оговориться, что не стоит писать те ключевые слова, которые не относятся к данному материалу или к странице вообще, от этого будет только хуже.</li>\r\n<li><strong>Мета-тег Robots</strong>&nbsp;– с помощью данного параметра мы можем указать, как должен действовать поисковый робот, посетивший данную страницу. Вариантов всего четыре:\r\n<ul>\r\n<li><em><strong>Index, Follow</strong></em>&nbsp;– разрешает роботу индексировать страницу и переходить по ссылкам</li>\r\n<li><em><strong>No index, follow</strong></em>&nbsp;– запрещает роботу индексировать страницу, но разрешает переход по ссылкам</li>\r\n<li><em><strong>Index, No follow</strong></em>&nbsp;– индексация разрешена, переход по ссылкам запрещен</li>\r\n<li><em><strong>No index, no follow</strong></em>&nbsp;– запрет индексации и перехода по ссылкам</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Хоть в настоящее время и бытует мнение, что&nbsp;<strong>поля Мета-тег Description и Мета-тег Keywords утратили свою значимость</strong>&nbsp;и не учитываются поисковыми роботами, но я все же рекомендую не оставлять их без внимания.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Параметры редактирования</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Следующая вкладка&nbsp;<strong>«Параметры редактирования»</strong>, давайте на неё нажмем и посмотрим, что в ней содержится.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/9.jpg" title="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3"><img alt="Вкладка «Параметры редактирования» на странице создания материала в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/9-fit-690x243.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Содержание данной вкладки не большое, состоит всего из четырех параметров –&nbsp;<strong>Показывать параметры публикации</strong>,&nbsp;<strong>Показывать параметры материала</strong>,&nbsp;<strong>Изображения и ссылки (панель управления)</strong>&nbsp;и&nbsp;<strong>Изображения и ссылки (сайт)</strong>. Что это такое? Ничего особенного, с помощью данных полей можно скрыть отображение, а соответственно и запретить вносить изменения в упомянутые выше вкладки. По умолчанию доступ разрешен, давайте попробуем в выпадающих списках выбрать значение «Нет» и посмотрим, что у нас получится (предварительно надо нажать на кнопку «Сохранить»). Результат я показал на скриншоте ниже:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/10.jpg" title="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками"><img alt="Страница создания материала в Joomla 3.7.3 со скрытыми дополнительными вкладками" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/10-fit-690x290.jpg" /></a></p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Не знаю, для каких целей это может потребоваться, возможно, на тот случай если Вы сами себе не доверяете, либо если потребуется скрыть данные поля от других пользователей. В общем, редактировать либо нет значения на данной вкладке дело сугубо личное.</p>\r\n<h3 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 18px;">Права доступа</h3>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Последняя вкладка&nbsp;<strong>«Права доступа»</strong>, на ней можно так сказать разделять и властвовать – разрешать или запрещать определенным категориям пользователей определенные действия со статьей. Таких действий всего три – удалить, изменить и изменить состояние. На начальном этапе советую ничего тут не трогать.</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Вот и подошел к концу урок по созданию материала Joomla, осталось только его сохранить. Для сохранения как вы уже наверно догадались, есть специальная кнопка «Сохранить» в верхней части страницы. Жмем на неё и видим сообщение об успешном сохранении, после этого можно закрыть материал, нажав на соответствующую кнопку. У меня получилось примерно так:</p>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;"><a class="cke-resize thumbnail zoomin magnific-popup-com_content_article_6" href="http://joom4all.ru/images/sozdanie_saitov/osnovi/8/11.jpg" title="Пример готового материала в Joomla 3.7"><img alt="Пример готового материала в Joomla 3.7" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/8/11-fit-690x359.jpg" /></a></p>\r\n<h2 style="font-family: ''Open Sans'', sans-serif; color: #333333; font-size: 22px;">Несколько советов правильного оформления материалов</h2>\r\n<p style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">Казалось бы, что тут сложного, написал статьи, сохранил и готово. Это конечно так, но я все же советую подойти к этому процессу со всей ответственность, чтобы в дальнейшем не пришлось изменять ранее написанные тексты.</p>\r\n<ul style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 13px;">\r\n<li><strong>Пишите стаью предварительно в текстовом редакторе</strong>, например в MS Word. Это поможет избежать возможных ошибок в тексте, даже если Вы невероятно грамотный человек и никогда не допускаете ошибок в написании, это будет не лишним, от опечаток никто не застрахован.</li>\r\n<li>После того как статья написана&nbsp;<strong>пользуйтесь специальной вставкой</strong>, которая убирает форматирование текста. Например, в редакторе JCK есть специальная кнопка «Вставить только текст» (можно просто скопировать текст из Word в блокнот, и только потом вставлять его в материал). Таким образом, Вы избавитесь от лишнего html мусора.</li>\r\n<li><strong>Не стоит переусердствовать при оформлении текста</strong>&nbsp;материала в редакторе Joomla – делать его слишком красивым, ярким, использовать различные шрифты и прочее. Это все лишнее, Весь текст должен оформляться при помощи файла стилей, а в самом материале стоит выделить только заголовки.</li>\r\n<li><strong>Заголовки в теле статьи не должны быть первого уровня</strong>&nbsp;(первый уровень это название материала).</li>\r\n<li><strong>Текст обязательно должен быть уникальным</strong>, это положительно скажется позиции сайта в поисковой выдаче.</li>\r\n</ul>\r\n<p>На этом данный урок я завершаю, а в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы">следующем уроке поговорим о меню</a>, именно с его помощью мы будем выводить материалы на страницы сайта.</p>', 1, 14, '2017-10-24 19:26:46', 38, '', '2017-10-24 19:38:31', 38, 0, '0000-00-00 00:00:00', '2017-10-24 19:26:46', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 3, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(7, 82, 'Меню в Joomla 3 - основы работы', 'menyu-v-joomla-3-osnovy-raboty', '<div itemprop="articleBody" style="font-family: Alice, serif;">\r\n<p style="font-size: 16px; text-align: justify;">В предыдущих уроках мы познакомились с понятием&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере" style="color: #fd8e32;">структуры сайта и создали необходимые категории</a>, а так же&nbsp;<a href="http://joom4all.ru/sites-creation/basics/creating-articles" title="Создание материалов в Joomla" style="color: #fd8e32;">создали свой первый материал в Joomla 3</a>. После создания материала Вы наверно уже обратили внимание, что на сайте он не появился, оно и понятно ведь мы для этого ничего не предпринимали. Так как же выводить созданные материалы на страницах сайта? Все очень просто, для этих целей в Joomla как раз и предусмотрено меню именно с его помощью мы сможем отображать на страницах нашего сайта заранее созданные материалы и другую информацию. Таким образом, в данном уроке поговорим о меню в Joomla.</p>\r\n', '\r\n<p style="font-size: 16px; text-align: justify;">Для работы с меню в панели управления Joomla предусмотрен специальный раздел, который так и называется «Меню». Как попасть в панель управления (админку сайта) и где находится раздел «Меню» рассказывать не буду, об этом говорилось в одном из предыдущих уроков посвященном<a href="http://joom4all.ru/sites-creation/basics/admin-panel" title="Админка сайта Joomla - первое знакомство" style="color: #fd8e32;">&nbsp;знакомству с админкой</a>. На всякий случай ниже на скриншоте обозначен раздел «Меню»:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/1.jpg" class="cke-resize thumbnail zoomin magnific-popup-com_content_article_17" title="Раздел «Меню»:в панели управления Joomla" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Раздел «Меню»:в панели управления Joomla" class="cke-resize" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/1-fit-690x186.jpg" style="height: 186px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">В разделе «Меню» по аналогии с материалами и категориями можно наблюдать «Менеджер меню» а так же все созданные пункты меню для сайта. Знакомство с меню предлагаю начать как раз с «Менеджера меню» Joomla, жмем по нему мышкой и попадаем на страницу «Менеджер меню: Все меню»:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/2.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Менеджер меню в панели управления Joomla" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Менеджер меню в панели управления Joomla" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/2-fit-690x137.jpg" style="height: 137px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Именно с этой страницы производятся все манипуляции с меню в Joomla, будь то создание, редактирование, копирование либо удаление. Давайте посмотрим, что содержится на странице менеджера меню. Тут все стандартно, примерно так же как в менеджере материалов или категорий. В верхней части имеются кнопки для работы с меню – создать, изменить, удалить, настройки и т.д. Слева кнопки быстрого доступа – меню и пункты меню. И наконец, в центральной части страницы отображаются все имеющиеся меню.</p>\r\n<p style="font-size: 16px; text-align: justify;">По умолчанию в Joomla уже создано одно единственное меню под названием «Main Menu», которое по умолчанию является главным, именно с него и начнем разбор полетов. Жмем по названию меню и попадаем на страницу пунктов меню – «Меню: Пункты (Main Menu)»:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/3.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Пункты главного меню в Joomla 3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Пункты главного меню в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/3-fit-690x161.jpg" style="height: 161px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">На данной странице мы видим, что у нас имеется один единственный пункт меню под названием «Home», тут же видно, что он относится к «Main Menu», а так же отмечен как главный. Чтобы не запутаться, давайте разберем, что такое меню и что такое его пункты, чтоб не писать много букв я покажу это на примере (скриншот ниже):</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/4.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Меню и пункт меню в виде схемы" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Меню и пункт меню в виде схемы" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/4-fit-690x400.jpg" style="height: 400px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Думаю, теперь стало понятно, что такое меню и что такое его пункты. Получается, по умолчанию в Joomla мы уже имеем главное меню (Main Menu), которое содержит один единственный пункт меню «Home» напротив которого стоит пометка, что он главный, следовательно, он ведет на главную страницу сайта. А теперь давайте рассмотрим устройство данного пункта меню и почему главная страница сайта пустая.</p>\r\n<h2 style="color: #fd8e32;">Редактирование пункта меню в Joomla</h2>\r\n<p style="font-size: 16px; text-align: justify;">Для того чтобы отредактировать имеющийся пункт меню в Joomla необходимо нажать мышкой по его названию (в панели управления) после чего мы попадаем на страницу его редактирования.</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/5.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Страница редактирования пункта меню в Joomla 3.7.3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Страница редактирования пункта меню в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/5-fit-690x357.jpg" style="height: 357px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Страница редактирования пункта меню не имеет особых отличий от страниц редактирования тех же материалов или категорий, так что сложностей тут быть не должно. Но есть и некоторые отличия, о которых стоит рассказать подробнее. На первой вкладке «Пункт меню» мы видим переключатель «Главная страница» и два варианта «Да» и «Нет». В нашем случае переключатель стоит в положение «Да» следовательно, данный пункт меню ведет на главную страницу сайта.</p>\r\n<p style="font-size: 16px; text-align: justify;">Следующий параметр «Тип пункта меню» именно он отвечает за то, что будет выводиться на странице сайта при нажатии на ссылку пункта меню. В нашем случае в качестве типа для пункта меню выбраны избранные материалы. Другими словами на главной странице сайта в нашем случае будут отображаться избранные материалы в виде блога (что такое избранные материалы и как они создаются,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/creating-articles" title="Создание материалов в Joomla" style="color: #fd8e32;">читайте в этой статье</a>). А выглядит это примерно вот так:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/6.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Пример избранных материалов на главной странице сайта" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Пример избранных материалов на главной странице сайта" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/6-fit-690x493.jpg" style="height: 493px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Остальные вкладки в данном случае актуальны только для выбранного типа пункта меню, рассматривать их не будем. На данном этапе просто поменяем его название и сохраним результат. Для наглядности я добавил к одному из материалов свойство «избранного» получившийся результат представлен на скриншоте ниже.</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/7.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Главная страница сайта с измененным именем пункта меню и избранным материалом" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Главная страница сайта с измененным именем пункта меню и избранным материалом" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/7-fit-690x403.jpg" style="height: 403px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Как отредактировать пункт меню разобрались, теперь давайте создадим новый собственный пункт меню.</p>\r\n<h2 style="color: #fd8e32;"><a name="sozdanie"></a>Как создать собственный пункт меню в Joomla 3</h2>\r\n<p style="font-size: 16px; text-align: justify;">Создание пункта меню стандартное - в менеджере меню жмем на зеленую кнопку «Создать» и на появившейся странице заполняем следующие поля:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/8.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Создание собственного пункта меню в Joomla 3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Создание собственного пункта меню в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/8-fit-690x317.jpg" style="height: 317px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Давайте разберем все по порядку. Изначально придумаем и заполним заголовок меню, я назвал его «Установка» (не спрашивайте почему). Далее заполняем алиас, можно конечно оставить без изменения, но в таком случае алиас получится такой – «ustanovka», мне этого не особо хочется, и я присвоил ему собственное название «installation».</p>\r\n<p style="font-size: 16px; text-align: justify;">Далее следует выбрать «Тип пункта меню», тут все сложнее, при нажатии на кнопку «Выбрать» вы увидите огромный список всевозможных типов, и этот список может увеличиваться в зависимости от установленных расширений. В рамках данного урока типы пункта меню рассматривать не будем, так как это достаточно обширная тема для разговоров и для них я решил выделить отдельные уроки (<a href="http://joom4all.ru/sites-creation/basics/types-of-menu-1" title="Типы пунктов меню Joomla" style="color: #fd8e32;">урок-1</a>&nbsp;и&nbsp;<a href="http://joom4all.ru/sites-creation/basics/types-of-menu-2" title="Типы пунктов меню Joomla – часть 2" style="color: #fd8e32;">урок-2</a>).</p>\r\n<p style="font-size: 16px; text-align: justify;">Для нашего нового типа меню давайте выберем список материалов категории, для этого жмем по кнопке «Выбрать» в списке находим раздел «Материалы» и выбираем «Список материалов категории».</p>\r\n<p style="font-size: 16px; text-align: justify;">Далее необходимо выбрать категорию, в которой содержатся интересующие нас материалы, в моем случае это «Установка» (что такое категория и как её создать читаем в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/website-structure" title="Структура сайта - разбираем на конкретном примере" style="color: #fd8e32;">этом уроке</a>).</p>\r\n<p style="font-size: 16px; text-align: justify;">На этом можно было бы, и закончить, но нас еще интересуют поля, которые расположены в правой части. Итак, по порядку. Первое поле «Меню», тут мы указываем, к какому меню будет отнесен только что созданный пункт, мы решили, что данный пункт будет в главном меню – оставляем без изменения. Далее «Родительский элемент» если оставить все без изменений, то пункт меню будет корневым, а если в качестве родительского выбрать уже существующий пункт, то данный пункт будет дочерним, т.е. вложенным. Чтобы стало понятнее, сохраним меню сначала корневым, а затем вложенным. Результат можно посмотреть на скриншоте:</p>\r\n<p style="font-size: 16px; text-align: center;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/9.jpg" class="thumbnail zoomin magnific-popup-com_content_article_17" title="Пункт меню первого и второго уровня в Joomla 3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Пункт меню первого и второго уровня в Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/9-fit-690x345.jpg" style="height: 345px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify;">Как видим, в первом случае пункты меню являются корневыми, а во втором вложены друг в друга и появляется только тогда, когда активен корневой пункт меню.<br />Аналогичным образом создаются любые другие пункты меню на сайте, но для большего понимания сути вопроса предлагаю перейти к следующему уроку посвященному&nbsp;<a href="http://joom4all.ru/sites-creation/basics/types-of-menu-1" title="Типы пунктов меню Joomla" style="color: #fd8e32;">типам пунктов меню</a>.</p>\r\n<p style="font-size: 16px; text-align: justify;">Забегая вперед, хочу обратить внимание, что в нашем случае меню получилось вертикальным, а такое отображение не всех устраивает. Как сделать горизонтальное меню читайте в уроке, посвященном&nbsp;<a href="http://joom4all.ru/sites-creation/basics/caps-for-site" title="Оформляем шапку сайта и создаем горизонтальное меню" style="color: #fd8e32;">редактированию шаблона Joomla 3</a>.</p>\r\n</div>', 1, 14, '2017-10-24 19:56:56', 38, '', '2017-10-24 20:24:53', 38, 0, '0000-00-00 00:00:00', '2017-10-24 19:56:56', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 4, '', '', 1, 7, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(8, 83, 'Типы пунктов меню Joomla ч.1 ', 'tipy-punktov-menyu-joomla-ch-1', '<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Приветствую Вас на втором уроке, посвященном меню Joomla 3. На этот раз мы не будем разбирать, что такое меню и для чего оно нужно, это обсудили в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/joomla-modules" title="Модули в Joomla 3 – что это такое и как с ними работать" style="color: #fd8e32;">прошлом уроке</a>, а на данном уроке поговорим об одном из главных свойств меню – о типах пункта меню. Это достаточно обширный вопрос ведь типов пункта меню даже в базовой сборке CMS Joomla более чем достаточно. Для начала давайте определимся, что такое тип пункта меню.</p>\r\n<h2 style="margin-right: 0px; margin-left: 0px; color: #fd8e32; background-color: #ffffff;">', 'Что такое тип пункта меню</h2>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Под типом пункта меню понимают тот элемент, который будет назначен данному меню. В качестве такого элемента может выступать материал, список материалов определенной категории, страница контактов, разделитель, компонент и многое другое. В качестве примера давайте вспомним&nbsp;<a href="http://joom4all.ru/sites-creation/basics/joomla-modules" title="Модули в Joomla 3 – что это такое и как с ними работать" style="color: #fd8e32;">прошлый урок</a>, когда мы редактировали стандартный пункт меню «Home», в нем в качестве типа пункта меню выступали избранные материалы. Именно избранные материалы это и есть один из тех самых элементов, о которых говорилось выше.</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Но избранные материалы далеко не единственный возможный тип пункта меню. А теперь давайте приступим к знакомству с другими типами.</p>\r\n<h2 style="margin-right: 0px; margin-left: 0px; color: #fd8e32; background-color: #ffffff;">Какие типы пункта меню существуют в Joomla 3</h2>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">В Joomla 3 как уже было сказано существует огромное количество всевозможных типов, которые могут быть назначены для меню, к тому же этот список может быть расширен после установки определенных расширений. По этой причине рассмотреть все возможные типы пункта меню невозможно в рамках одного урока. И я расскажу только о тех, которые присутствуют в стандартной сборке CMS Joomla 3.</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Давайте откроем полный список типов пункта меню и посмотрим, что там у нас есть. Для тех, кто не помнит где это посмотреть, напоминаю – открываем один из пунктов меню для редактирования, либо создаем новый пункт меню и в поле «Тип пункта меню*» жмем на кнопку «Выбрать».<br />&nbsp;</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/1.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Для того чтобы посмотреть список всевозможных типов пункта меню нужно открыть пункт меню для редактирования" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для того чтобы посмотреть список всевозможных типов пункта меню нужно открыть пункт меню для редактирования" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/1-fit-690x359.jpg" style="height: 359px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">В качестве примера я привел скриншот страницы редактирования пункта меню, который ведет на главную страницу сайта. После нажатия на кнопку «Выбрать» перед нами в модальном окне откроется весь перечень доступных в Joomla типов пункта меню:</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/2.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Типы пунктов меню которые присутствуют в базовой сборке Joomla 3.7" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Типы пунктов меню которые присутствуют в базовой сборке Joomla 3.7" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/2-fit-690x389.jpg" style="height: 389px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Да, список достаточно большой, к тому же каждый из типов имеет вложения. А теперь давайте разберем каждый из типов по порядку, начиная с первого, а первый у нас это тип пункта меню «Контакты».</p>\r\n<h3 style="margin-right: 0px; margin-left: 0px; color: #fd8e32; background-color: #ffffff;">Тип пункта меню «Контакты»</h3>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Первый в списке тип пункта меню имеет название «Контакты», он в свою очередь содержит четыре вложенных типа, смотрите на скриншот ниже:</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/3.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Тип пункта меню Joomla &quot;Контакты&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню Joomla &quot;Контакты&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/3-fit-690x161.jpg" style="height: 161px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Что такое контакты в Joomla мы еще не знаем, об этом я обязательно расскажу в следующих уроках. Если совсем коротко, то контакты чем-то напоминают&nbsp;<a href="http://joom4all.ru/sites-creation/basics/creating-articles" title="Создание материалов в Joomla" style="color: #fd8e32;">материалы в Joomla</a>, они так же могут быть избранными, а так же иметь свои категории. Но в отличие от материала контакт содержит информацию лишь о конкретных пользователях сайта (если можно так сказать). Таким образом, выбрав один из перечисленных типов пункта меню можно выводить на страницы сайта, как отдельный контакт, так и все контакты, относящиеся к определенной категории. Рассмотрим все типы подробнее.</p>\r\n<ul style="padding-top: 0px; margin-right: 0px; margin-left: 25px; font-family: Alice, serif; background-color: #ffffff;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Избранные контакты</strong>&nbsp;– при помощи данного типа пункта меню можно выводить на страницы сайта только те контакты, которые помечены как избранные.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Контакт</strong>&nbsp;– данный тип выводит сведения только об одном выбранном контакте.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список категорий контактов</strong>&nbsp;– по названию уже все стало понятно, данный тип пункта меню выводит список всех категорий, которые вложены в выбранную категорию контактов.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список контактов заданной категории</strong>&nbsp;– в отличие от предыдущего типа пункта меню, который выводит список категорий, данный тип выводит все контакты, которые входят в выбранную категорию.</li>\r\n</ul>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">В каком случае применяются данные типы пункта меню? Допустим, вы создаете корпоративный сайт, в котором необходимо предоставить пользователям контактную информацию для связи с сотрудниками компании. В данном случае можно создать список контактов и разбить их на определенные категории – руководство, отдел по работе с персоналом, отдел продаж и т.д. Вот тут и приходит на помощь данный тип пункта меню, можно выводить на страницах сайта отдельно взятый контакт, либо, сгруппировав их по категориям выводить в удобном для пользователей списке.</p>\r\n<h2 style="margin-right: 0px; margin-left: 0px; color: #fd8e32; background-color: #ffffff;">Тип пункта меню «Ленты новостей»</h2>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Следующий в списке тип пункта меню носит название «Ленты новостей». Судя по названию должно быть понятно что речь в данном случае пойдет о RSS лентах.&nbsp;</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/4.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Тип пункта меню Joomla - &quot;Ленты новостей&quot; " style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню Joomla - &quot;Ленты новостей&quot; " src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/4-fit-690x150.jpg" style="height: 150px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Тип пункта меню «Ленты новостей» содержит три вложенных пункта. Тут все аналогично контактам – можно выводить как отдельную ленту новостей, так же список их категорий и все новости из выбранной категории.</p>\r\n<ul style="padding-top: 0px; margin-right: 0px; margin-left: 25px; font-family: Alice, serif; background-color: #ffffff;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Лента новостей</strong>&nbsp;– выводит одну конкретно указанную RSS&nbsp;ленту</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список всех категорий лент новостей</strong>&nbsp;– отображает все имеющиеся категории, которые были созданы для лент новостей в виде списка</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список лент новостей в категории</strong>&nbsp;– выводит все имеющиеся ленты новостей из указанной категории.</li>\r\n</ul>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">С лентами новостей разобрались, идем дальше, а дальше у нас самый часто используемый тип пункта меню – «Материалы».</p>\r\n<h3 style="margin-right: 0px; margin-left: 0px; color: #fd8e32; background-color: #ffffff;">Тип пункта меню «Материалы»</h3>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Данный тип пункта меню используется наиболее часто, ведь именно с его помощью на страницах сайта выводятся статьи, как по отдельности, так и в виде списка. Но вариантов вывода материалов на страницы сайта гораздо больше двух и их мы сейчас рассмотрим.</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/5.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Тип пункта меню &quot;Материалы&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню &quot;Материалы&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/5-fit-690x260.jpg" style="height: 260px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Рассмотрим всё это многообразие по порядку:</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Архивные материалы&nbsp;</strong>– если выбрать данный тип пункта меню то на странице сайта будут выведены те материалы, которые были помечены как архивные. Например, на Вашем сайте накопилось множество статей, которые по той или иной причине на данный момент не актуальны, их можно пометить как архивные и выводить на отдельной странице.</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Блог категории</strong>&nbsp;– это достаточно распространенный тип пункта меню, его довольно часто используют на страницах сайта. С его помощью можно вывести материалы в виде блога в одном или нескольких столбцах. В качестве примера можно посмотреть на скриншот.</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9/6.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Материалы на сайте выведенные в виде блога из определенной категории" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Материалы на сайте выведенные в виде блога из определенной категории" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9/6-fit-690x493.jpg" style="height: 493px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Избранные материалы</strong>&nbsp;– данный тип пункта меню ничем не отличается от предыдущего, разве что вместо того чтобы выводить материалы с выбранной категории он выводит материалы которые помечены как избранные.</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Материал</strong>&nbsp;– если пункту меню будет назначен данный тип, то на странице сайта будет выведен только указанный материал. Так же очень распространен.</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Создать материал</strong>&nbsp;– с помощью данного типа пункта меню можно вывести на странице сайта специальную форму для добавления материала. Это может быть полезно в том случае, когда необходимо добавлять материал, не заходя в админку сайта (например, дать возможность пользователям сайта создавать новые статьи, при этом, не допуская их к панели управления).</p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Список всех категорий</strong>&nbsp;– при помощи данного типа меню можно вывести все категории, которые входят в выбранную в виде списка. На сайте это будет выглядеть примерно следующим образом (смотрите скриншот).</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/6.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Страница сайта на которой выведен список всех категорий" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Страница сайта на которой выведен список всех категорий" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/6-fit-690x269.jpg" style="height: 269px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><strong>Список материалов категории</strong>&nbsp;– данный тип пункта меню выводит все материалы выбранной категории в виде обычного списка. При этом по умолчанию будут отображены только заголовки материалов и количество просмотров (пример на скриншоте).</p>\r\n<p style="font-size: 16px; text-align: center; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_1/7.jpg" class="thumbnail zoomin magnific-popup-com_content_article_31" title="Если типом пункта меню указать список материалов категории то они будут выведены следующим образом" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Если типом пункта меню указать список материалов категории то они будут выведены следующим образом" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_1/7-fit-690x269.jpg" style="height: 269px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; margin-right: 0px; margin-left: 0px; font-family: Alice, serif; background-color: #ffffff;">Тип пункта меню «Список материалов категории» был последним в разделе «Материал». На этом я решил поставить точку в данном уроке, а продолжение разбора типов пункта меню читайте в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/types-of-menu-2" title="Типы пунктов меню Joomla – часть 2" style="color: #fd8e32;">следующем уроке</a>.</p>', 1, 14, '2017-10-24 20:05:11', 38, '', '2017-10-24 20:05:11', 0, 0, '0000-00-00 00:00:00', '2017-10-24 20:05:11', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 1, '', '', 1, 3, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(9, 84, 'Типы пунктов меню Joomla ч.2 – менеджер настроек и пользователей, поиск, обёртка', 'tipy-punktov-menyu-joomla-ch-2-menedzher-nastroek-i-polzovatelej-poisk-objortka', '<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Продолжаем знакомство с типами пункта меню Joomla 3, начатое в предыдущем уроке. В прошлый раз мы остановились на типе пункта меню «Материалы», следующим по порядку типом пункта меню идет&nbsp;<strong>«Менеджер настроек»</strong>. Начнем именно с него.</p>\r\n<h3 style="color: #fd8e32; font-size: 18px;">Тип пункта меню «Менеджер настроек»</h3>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Чтобы понять, для чего нужен данный тип пункта меню, давайте рассмотрим, какие у него есть вложения их всего два:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/1.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню Joomla 3 - &quot;Менеджер настроек&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню Joomla 3 - &quot;Менеджер настроек&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/1-fit-690x176.jpg" style="height: 176px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Взглянув на скриншот, сразу становится понятно, за что отвечает каждый (а их всего два) из типов пункта меню «Менеджер настроек»</p>\r\n<ul style="color: #333333; font-family: Alice, serif; font-size: 13px;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Параметры конфигурации сайта</strong>&nbsp;– данный тип пункта меню выводит на странице сайта параметры его конфигурации (метаданные, параметры SEO и т.д.)</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Параметры шаблона</strong>&nbsp;– отображает параметры шаблона. Работает данный тип пункта меню только в том случае, когда используемый шаблон сайта поддерживает отображение его параметров.</li>\r\n</ul>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Стоит отметить, что данные типы пункта меню не только отображают информацию о сайте либо шаблоне, но и позволяют вносить в них изменения, следовательно, для доступа к таким страницам нужно обладать правами администратора. Кстати, для того чтобы ссылка на конфигурацию сайта либо шаблона была скрыта от лишних глаз, можно разграничить доступ к данному пункту меню как на скриншоте ниже.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/2.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Для скрытия пункта меню от пользователей можно воспользоваться параметром &quot;Доступ&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для скрытия пункта меню от пользователей можно воспользоваться параметром &quot;Доступ&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/2-fit-690x329.jpg" style="height: 329px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Возможно, данные типы пункта меню будут полезны особо ленивым администраторам сайта, которые захотят внести какие либо изменения, не входя в панель управления.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Для наглядности давайте посмотрим, как выглядит страница, на которой отображены параметры конфигурации сайта:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/3.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Пример страницы на которой при помощи типа пункта меню выведена конфигурация сайта" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Пример страницы на которой при помощи типа пункта меню выведена конфигурация сайта" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/3-fit-690x696.jpg" style="height: 696px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">А так выглядит страница, отображающая и позволяющая редактировать некоторые настройки шаблона сайта.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/4.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Страница сайта на которой отображены настройки шаблона" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Страница сайта на которой отображены настройки шаблона" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/4-fit-690x525.jpg" style="height: 525px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">С «Менеджером настроек» разобрались, продолжаем знакомство с другими типами пункта меню и следующий на очереди тип – «Метки».</p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Метки»&nbsp;</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Семейство типов пункта меню под общим названием «Метки» будет полезно тем, кто использует метки на своем сайте. Что такое метки описывать не буду, этому будет посвящён отдельный урок, скажу только что «Метки» это новый компонент который появился в Joomla, начиная с третьей версии. С помощью меток можно более детально классифицировать статьи (материалы), но об этом позднее, а сейчас давайте просто посмотрим, какие типы пунктов меню доступны для меток.&nbsp;Их всего три:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/5.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню «Метки» в Joomla 3.7.3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню «Метки» в Joomla 3.7.3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/5-fit-690x226.jpg" style="height: 226px; width: 690px;" /></a></p>\r\n<ul style="color: #333333; font-family: Alice, serif; font-size: 13px;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список меток</strong>&nbsp;– при помощи данного типа пункта меню можно вывести на страницу сайта все имеющиеся метки, либо всех потомков указанной родительской метки.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список элементов</strong>&nbsp;– в этом случае будут выведены все элементы, которым была назначена заданная метка. Это могут быть как материалы, так и категории.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Список элементов (компактный)</strong>&nbsp;– как и предыдущий пункт, но в компактном представлении.</li>\r\n</ul>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Обёртка (Wrapper)»</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Следующий на очереди тип пункта меню - «Обёртка (Wrapper)», который содержит один единственный одноименный подпункт:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/6.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню «Обёртка (Wrapper)»" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню «Обёртка (Wrapper)»" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/6-fit-690x204.jpg" style="height: 204px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Для чего нужен подобный тип пункта меню (Wrapper) думаю понятно – для отображения страницы сайта в отдельном фрейме, причем со своей собственной полосой прокрутки, а вот где это может пригодиться, моей фантазии не хватает. Для вывода страницы при помощи обёртки нужно указать её прямой адрес, кроме того можно выводить как страницы своего так и стороннего сайта. Выглядит сие чудо следующим образом:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/7.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Страница сайта на которой при помощи типа пункта меню &quot;Обёртка&quot; выведена страница Яндекс.Погода" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Страница сайта на которой при помощи типа пункта меню &quot;Обёртка&quot; выведена страница Яндекс.Погода" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/7-fit-690x452.jpg" style="height: 452px; width: 690px;" /></a></p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Поиск»</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Очередной, но не последний тип пункта меню, содержащий единственный пункт - «Форма поиска и список результатов поиска».</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/8.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню Joomla 3.7.3 - «Поиск»" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню Joomla 3.7.3 - «Поиск»" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/8-fit-690x105.jpg" style="height: 105px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">При помощи данного типа пункта меню можно выводить на страницу сайта специальную форму для поиска с расширенными параметрами. Выглядит это следующим образом:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/9.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Форма поиска с результатами доступ к которой осуществляется при помощи определенного типа пункта меню Joomla 3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Форма поиска с результатами доступ к которой осуществляется при помощи определенного типа пункта меню Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/9-fit-690x373.jpg" style="height: 373px; width: 690px;" /></a></p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Пользователи»</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Тип пункта меню «Пользователи» (другое название «Менеджер пользователей», все зависит от установленной версии и языкового пакета Joomla) состоит из семи вложенных типов необходимых для различных манипуляций связанных с пользователями сайта.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/10.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню в Joomla 3 - &quot;Пользователи&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню в Joomla 3 - &quot;Пользователи&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/10-fit-690x237.jpg" style="height: 237px; width: 690px;" /></a></p>\r\n<ul style="color: #333333; font-family: Alice, serif; font-size: 13px;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Восстановление имени пользователя</strong>&nbsp;– тут все просто, перейдя по пункту меню с данным типом, пользователь попадет на страницу восстановления забытого логина. Для этого необходимо будет ввести e-mail указанный при регистрации, именно на него будет выслан забытый логин.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Выход</strong>&nbsp;– данный тип пункта меню используется для выхода с сайта как зарегистрированного пользователя. Другими словами если вам захотелось «Разлогиниться» то вам сюда.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Изменение пароля</strong>&nbsp;– в данном случае суть точно такая же, как с восстановлением имени пользователя. Пользователь вводит указанный при регистрации e-mail, на который будет отправлен специальный проверочный код. Далее с его помощью можно будет ввести новый пароль. Полезно когда пользователь забыл пароль.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Изменить профиль пользователя</strong>&nbsp;– при помощи данного пункта меню пользователь может поменять либо отредактировать свои личные данные, такие как имя пользователя, пароль, адрес электронной почты, язык, шаблон сайта (по возможности) и т.п.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Профиль пользователя</strong>&nbsp;– служит для вывода информации о пользователе, но в отличие от предыдущего пункта редактирование не предусмотрено.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Форма авторизации&nbsp;</strong>– при нажатии на пункт меню с данным типом, пользователь будет направлен на страницу, где ему будет предложено ввести свой логин и пароль для авторизации на сайте.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Форма регистрации</strong>&nbsp;– данный тип пункта меню необходим для регистрации новых пользователей на сайте. На странице необходимо будет ввести имя, логин, пароль и адрес электронной почты.&nbsp;</li>\r\n</ul>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Системные ссылки»</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Достаточно интересный тип пункта меню в Joomla, который содержит четыре вложенных типа. С их помощью можно перенаправлять пользователей на внешние страницы, выводить разделители и т.п.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/11.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню «Системные ссылки»" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню «Системные ссылки»" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/11-fit-690x260.jpg" style="height: 260px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Рассмотрим перечисленные типы пункта меню подробнее:</p>\r\n<ul style="color: #333333; font-family: Alice, serif; font-size: 13px;">\r\n<li style="font-size: 16px; text-align: justify;"><strong>Внешний URL</strong>&nbsp;– при помощи данного типа пункта меню можно перенаправлять пользователей, как на внутреннюю страницу сайта, так и на другой сайт по прямой ссылке. Стоит отметить что адрес (или URL) страницы указывается в абсолютном формате, что несколько неудобно для указания внутренних страниц сайта.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Заголовок</strong>&nbsp;– достаточно полезный тип пункта меню, с его помощью удобно делать выпадающие меню, в которых родительский элемент не должен являться ссылкой на страницу. Указав пункту меню, что он будет иметь тип «Заголовок» он будет выведен как обычный не кликабельный заголовок.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Псевдоним пункта меню</strong>&nbsp;– очень полезный тип пункта меню, он способен перенаправлять пользователя на уже существующий пункт меню. Для чего это использовать? Давайте рассмотрим пример, допустим, у вас имеется главное меню с определенными пунктами, и есть несколько дополнительных, которые имею свою иерархию. Если станет необходимо с главного меню сделать ссылку на существующий, то для того чтобы не создавать дублей как раз и был придуман данный тип пункта меню. Он просто перенаправляет пользователя на уже существующее меню, при этом, не создавая дублей страниц.</li>\r\n<li style="font-size: 16px; text-align: justify;"><strong>Разделитель</strong>&nbsp;– тут все в точности так же как в случае с типом пункта меню «Заголовок», различия лишь в оформлении и классах применимых к пункту меню.</li>\r\n</ul>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Тип пункта меню «Умный поиск»</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">И последний в списке типов пункта меню, который присутствует в базовой сборке Joomla 3.7 – «Умный поиск».</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/12.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Тип пункта меню «Умный поиск»" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Тип пункта меню «Умный поиск»" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/12-fit-690x297.jpg" style="height: 297px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Что он делает и чем отличается от обычного поиска? На первый вопрос, что делает умный поиск ответ простой – в точности то же самое что и обычный, а вот чем они отличаются, сейчас попытаюсь объяснить. Умный поиск в отличие от обычного удобен тем, что когда пользователь начинает набирать искомую фразу в строку поиска ему в выпадающем списке будут предложены возможные варианты, которые можно выбрать, не набирая всю фразу целиком. Однако для того чтобы «умный поиск» функционировал необходимо включить соответствующий плагин на странице менеджера плагинов.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_2/13.jpg" class="thumbnail zoomin magnific-popup-com_content_article_32" title="Пример использования &quot;Умного поиска&quot; на сайте Joomla 3" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Пример использования &quot;Умного поиска&quot; на сайте Joomla 3" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_2/13-fit-690x466.jpg" style="height: 466px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Вот и подошел к концу второй урок посвященный типам пункта меню. Мы рассмотрели только базовые типы, однако их количество может быть увеличено в зависимости от того какие дополнительные компоненты были установлены в Joomla.&nbsp;</p>', '', 1, 14, '2017-10-24 20:15:28', 38, '', '2017-10-24 20:16:04', 38, 38, '2017-10-25 19:59:08', '2017-10-24 20:15:28', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 2, 2, '', '', 1, 1, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `qhk3j_content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(10, 85, 'Работа с меню - добавление, копирование и перенос', 'rabota-s-menyu-dobavlenie-kopirovanie-i-perenos', '<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На предыдущих уроках мы рассмотрели вопрос,&nbsp;<a href="http://joom4all.ru/sites-creation/basics/sozdanie-menyu" title="Меню в Joomla 3 - основы работы" style="color: #fd8e32;">что такое меню в Joomla 3</a>, научились создавать и редактировать пункты меню, а так же рассмотрели достаточно обширный&nbsp;<a href="http://joom4all.ru/sites-creation/basics/types-of-menu-1" title="Типы пунктов меню Joomla - контакты, ленты новостей, материалы" style="color: #fd8e32;">вопрос посвященный типам пункта меню</a>. Данный урок будет заключительным, на сей раз, мы поговорим, как создаются дополнительные меню в Joomla 3, а так же разберем процесс переноса пунктов меню, как по отдельности, так и при помощи пакетной обработки. Начнем с добавления нового меню в Joomla.</p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Как добавить новое меню в Joomla 3</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Для того чтобы создать или другими словами добавить новое меню в Joomla 3 как обычно предусмотрена специальная кнопка «Создать», которую необходимо нажать в «Менеджере меню: Все меню»:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/1.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Для добавления нового меню необходимо нажать на кнопку &quot;Создать&quot; в менеджере меню" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для добавления нового меню необходимо нажать на кнопку &quot;Создать&quot; в менеджере меню" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/1-fit-690x149.jpg" style="height: 149px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">После нажатия на кнопку «Создать» мы попадаем на страницу создания нового меню в Joomla 3. Выглядит данная страница следующим образом:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/2.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Создание нового меню в Joomla 3, заполняем поля заголовок и системное имя (тип меню). Жмем &quot;Сохранить и закрыть&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Создание нового меню в Joomla 3, заполняем поля заголовок и системное имя (тип меню). Жмем &quot;Сохранить и закрыть&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/2-fit-690x193.jpg" style="height: 193px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Заполняем обязательные поля такие как «Заголовок» и «Тип меню», а так же выбираем, для чего у нас будет предназначено данное меню для сайта либо для панели управления. Обратите внимание на вкладку «Права доступа к меню», она нужна для разграничения прав доступа к данному меню, можно указать каким пользователям будет разрешено администрировать данное меню. После того как изменения будут внесены жмем на кнопку «Сохранить и закрыть», готово, меню создано. Теперь можно&nbsp;<a href="#sozdanie" title="Как создать пункт меню в Joomla 3" style="color: #fd8e32;">создать для него необходимые пункты меню</a>.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">После создания второго и последующих меню может появиться необходимость переноса определенных пунктов с одного меню в другое. Этот процесс не сложный, а производить его можно несколькими способами, которые мы сейчас и рассмотрим.&nbsp;</p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Как перенести пункты меню с одного меню в другое</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Для чего может понадобиться переносить пункты меню? Допустим, у Вас уже имеется меню, в котором создано определенное количество пунктов, потом Вы решили создать другое меню, в которое необходимо поместить пункты из ранее созданного меню. В этом случае чтобы не создавать новые пункты и не удалять старые предусмотрен процесс переноса пунктов меню с одного меню в другое.&nbsp;<strong>Процесс переноса пунктов меню</strong>с одного меню в другое достаточно простой процесс и займет не больше минуты.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Процесс переноса можно осуществить несколькими способами, либо переносить каждый пункт по отдельности, либо при помощи пакетной обработкой перенести сразу несколько предварительно выбранных пунктов. Давайте разберем каждый из вариантов подробнее.&nbsp;</p>\r\n<h3 style="color: #fd8e32; font-size: 18px;">Перенос одного пункта меню</h3>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Данный вариант будет полезен, если нет необходимости переносить большое количество пунктов меню. Для того&nbsp;<strong>чтобы перенести только один пункт меню</strong>&nbsp;необходимо открыть данный пункт для редактирования и на первой вкладке «Пункт меню» смотрим в правую часть страницы. Нас интересуют пункты «Меню» и «Родительский элемент», именно изменяя эти параметры можно осуществить процесс переноса:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/3.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Для переноса пункта меню с одного меню в другое можно воспользоваться разделами полями &quot;Меню&quot; и &quot;Родительский элемент&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для переноса пункта меню с одного меню в другое можно воспользоваться разделами полями &quot;Меню&quot; и &quot;Родительский элемент&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/3-fit-690x192.jpg" style="height: 192px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На данной странице мы видим, что выбранный пункт меню находится в главном меню и не имеет родительских элементов. Изменив значение поля «Меню» мы перенесем данный пункт в любое другое указанное меню, а если изменить «Родительский элемент» то можно сделать данный пункт вложенным либо корневым. Для наглядности смотрите на скриншот ниже:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/4.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Переносим пункт меню с одного меню в другое" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Переносим пункт меню с одного меню в другое" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/4-fit-690x185.jpg" style="height: 185px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">После того как настройки будут изменены жмем на кнопку «Сохранить и закрыть» и проверяем результат. Вот и все, процесс переноса завершен. Но данный метод хорош только в том случае, когда необходимо перенести только один пункт меню, при переносе нескольких пунктов меню подобным способом процесс может затянуться. К тому же данный метод совершенно не пригоден для копирования пункта меню. Для этих целей можно воспользоваться другим способом - пакетной обработкой.</p>\r\n<h3 style="color: #fd8e32; font-size: 18px;">Перенос либо копирование нескольких пунктов меню при помощи пакетной обработки</h3>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Мы уже поняли, что для переноса нескольких пунктов меню описанный выше метод не годится, а про копирование пунктов меню и говорить нечего. Сейчас рассмотрим другой способ, который позволяет не только переносить сразу несколько пунктов меню с одного меню в другое, но и копировать их. Для этих целей в Joomla предусмотрена специальная функция – пакетная обработка.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Для того чтобы воспользоваться пакетной обработкой заходим в меню, с которого будет осуществляться перенос (либо копирование) и напротив каждого пункта меню, который необходимо перенести ставим галочки. Затем нажимаем на кнопку «Пакетная обработка».&nbsp;</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/5.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Для переноса либо копирования пунктов меню необходимо их выделить и нажать на кнопку &quot;Пакетная обработка&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для переноса либо копирования пунктов меню необходимо их выделить и нажать на кнопку &quot;Пакетная обработка&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/5-fit-690x275.jpg" style="height: 275px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На странице пакетной обработки пунктов меню выбираем то самое меню, в которое необходимо осуществить перенос, так же по необходимости выбираем родительский элемент. После того как было выбрано новое место расположение пунктов меню станет доступен выбор действий которые можно с ними производить – копирование или перенос. Так же при необходимости можно изменить уровни доступа и язык.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/6.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Выбираем меню в которое необходимо осуществить перенос или копирование пунктов и жмем кнопку &quot;Выполнить&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Выбираем меню в которое необходимо осуществить перенос или копирование пунктов и жмем кнопку &quot;Выполнить&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/6-fit-690x236.jpg" style="height: 236px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Жмем на кнопку «Выполнить» и проверяем результат. Хочу отметить, что данный метод гораздо удобнее и не занимает много времени.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На этом можно было бы завершить урок, но тут есть один момент, который стоит рассмотреть – как выводить только что созданное меню на сайт.</p>\r\n<h2 style="color: #fd8e32; font-size: 22px;">Как вывести дополнительное меню на сайт</h2>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Как вы уже наверно заметили после того как было создано новое меню на сайте оно так и не появилось. Все дело в том, что для отображения меню на сайте используется специальный модуль, который мы естественно не назначили нашему новому меню. И если по умолчанию для отображения главного меню уже имеется предустановленный модуль, то для любого вновь добавленного меню необходимо создавать дополнительные модули. Этим мы сейчас и займемся.</p>\r\n<h3 style="color: #fd8e32; font-size: 18px;">Создание модуля «Меню»</h3>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Что такое модули можно прочитать в&nbsp;<a href="http://joom4all.ru/sites-creation/basics/joomla-modules" title="Модули в Joomla 3 – что это такое и как с ними работать" style="color: #fd8e32;">этом уроке</a>, сейчас же просто создадим стандартный модуль «Меню» и присвоим ему определенную позицию на сайте. Для этого переходим в раздел панели управления «Расширения» и в выпадающем списке жмем на ссылку «Модули» (или «Менеджер модулей», название зависит от установленного языкового пакета):</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/7.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Для доступа к менеджеру модулей переходим в раздел &quot;Расширения&quot; и жмем по ссылке &quot;Модули&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Для доступа к менеджеру модулей переходим в раздел &quot;Расширения&quot; и жмем по ссылке &quot;Модули&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/7-fit-690x133.jpg" style="height: 133px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На открывшейся странице «Модули (сайт)» мы можем заметить, что для главного меню уже имеется свой модуль, теперь надо создать точно такой же для нашего нового меню. Для создания модуля «Меню» жмем на кнопку «Создать»:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/8.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Создаем дополнительный модуль. Для этого жмем по кнопке &quot;Создать&quot;" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Создаем дополнительный модуль. Для этого жмем по кнопке &quot;Создать&quot;" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/8-fit-690x173.jpg" style="height: 173px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Перед нами откроется новая страница, на которой будет достаточно большой список всевозможных модулей, которые можно разместить на сайте. Но нас интересует модуль «Меню», его и выбираем.</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/9.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Выбираем модуль &quot;Меню&quot; из списка доступных модулей" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Выбираем модуль &quot;Меню&quot; из списка доступных модулей" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/9-fit-690x190.jpg" style="height: 190px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">На открывшейся странице «Модули: Меню» редактируем модуль примерно так, как показано на скриншоте ниже:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/10.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Редактируем модуль &quot;Меню&quot; для сайта на Joomla 3.7" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Редактируем модуль &quot;Меню&quot; для сайта на Joomla 3.7" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/10-fit-690x360.jpg" style="height: 360px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Что тут необходимо заполнить? Все стандартно, заполняем заголовок для модуля, он будет отображен в панели управления, а если выбрать значение «Показать заголовок» то и на сайте тоже. Далее выбираем то самое меню, которое хотим вывести на сайте, а так же базовый пункт меню. Если пункты меню содержат вложения, то необходимо указать их начальный и конечный уровень. В моем случае начальный уровень был выбран второй, таким образом, меню будет отображаться на сайте, начиная со второго уровня не учитывая первый.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">А теперь надо определиться с местом вывода нового меню, для этого служит специальный параметр «Позиция». Позиций вывода модулей на сайт может быть достаточно много, их количество зависит от шаблона. В данном примере используется шаблон «Protostar» и я решил вывести новое меню в правой части сайта. Если Вы решили поступить так же, то выбирайте из списка позиций позицию с именем «Right [position-7]». Для того чтобы отделить главное меню от дополнительного я присвоил ему позицию с именем «position-1». Сохраняем изменения и смотрим, что у нас получилось.</p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">В панели управления в разделе «Менеджер модулей» мы видим вновь созданный модуль:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/11.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Мы создали новый модуль &quot;Меню&quot; и видим его в панели управления" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Мы создали новый модуль &quot;Меню&quot; и видим его в панели управления" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/11-fit-690x184.jpg" style="height: 184px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">А так выглядит новое меню на сайте Joomla:</p>\r\n<p style="font-size: 16px; text-align: center; color: #333333; font-family: Alice, serif;"><a href="http://joom4all.ru/images/sozdanie_saitov/osnovi/9_3/12.jpg" class="thumbnail zoomin magnific-popup-com_content_article_30" title="Новое меню выведено на сайт в определенной позиции при помощи специального модуля" style="color: #fd8e32; display: inline-block; cursor: url(''/media/plg_content_mavikthumbnails/images/zoomin.cur''), pointer; max-width: 100%;"><img alt="Новое меню выведено на сайт в определенной позиции при помощи специального модуля" src="http://joom4all.ru/images/mini/images/sozdanie_saitov/osnovi/9_3/12-fit-690x487.jpg" style="height: 487px; width: 690px;" /></a></p>\r\n<p style="font-size: 16px; text-align: justify; color: #333333; font-family: Alice, serif;">Меню создано, а вместе с ним и модуль, на этом я завершаю последний урок посвященный работе с меню в Joomla 3. До встречи на следующих уроках.</p>', '', 1, 14, '2017-10-25 20:18:44', 38, '', '2017-10-25 20:18:44', 0, 0, '0000-00-00 00:00:00', '2017-10-25 20:18:44', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 0, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 1, '*', ''),
(11, 90, 'Git для чайника. Команды которые помогут начать работу', 'git-dlya-chajnika-komandy-kotorye-pomogut-nachat-rabotu', '<div class="info info-top t-gray600 t90" style="color: #757575; font-size: 0.9rem;"><span class="i-bookmark" title="Рубрика записи" data-mce-mark="1"><a href="http://rgblog.ru/category/article" style="background-color: transparent; color: #556677;">Полезные статьи</a></span></div>\r\n<p>&nbsp;</p>\r\n<div style="color: #222222; font-family: ''Open Sans'', ''Helvetica Neue'', Helvetica, Arial, sans-serif; font-size: 16px;"><img src="http://rgblog.ru/site/rgblog/uploads/_pages/105/mini/git-200-130.jpg" class="cpict" alt="Git для чайника. Команды которые помогут начать работу." style="border-width: 1px; border-style: solid; border-color: #d5d5d5; display: block; float: left; margin: 0px 1.25em 1.25em 0px; padding: 1px; background-image: initial; background-position: initial; background-repeat: initial; background-attachment: initial;" />\r\n<p style="margin-top: 10px; margin-bottom: 14px;">Многим трудно начать работать с&nbsp;<span style="font-weight: bold;">Git</span>, ведь не все привыкли работать с командной строкой, но именно ее лучше всего использовать при работе с&nbsp;<em>репозиторием Git</em>. И сегодня я расскажу о самом простом, так сказать&nbsp;<span style="font-weight: bold;">Git для чайника</span>, чтобы помочь освоиться Вам с этой системой.&nbsp;<em>Git</em>представляет собой систему контроля версий, которая позволяет в нужный момент откатиться на старую версию, если вы "<i>наломали дров</i>".</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">С помощью Git Вы сможете размещать свой код на&nbsp;<span style="font-weight: bold;">GitHub</span>,&nbsp;<span style="font-weight: bold;">BitBucket</span>&nbsp;и&nbsp;<span style="font-weight: bold;">Google Code</span>.</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">Вы всегда сможете скачать код своего проекта на компьютер, изменить его и залить обратно, чтобы он стал доступен Вашим коллегам.</p>\r\n<h2 style="margin-top: 30px; margin-bottom: 10px; font-weight: 400;">С чего начать?</h2>\r\n<p style="margin-top: 15px; margin-bottom: 14px;">Нам понадобиться программа&nbsp;<span style="font-weight: bold;">Git Bash</span>, это шелл сделанный на основе Cygwin, поэтому возможно использование Unix-команд, вроде ls, cd, mkdir. Скачать его можно по следующей ссылке&nbsp;<a href="http://git-scm.com/" style="background-color: transparent; color: #556677;"></a><a href="http://git-scm.com/">http://git-scm.com/</a>.</p>\r\n<h2 style="margin-top: 30px; margin-bottom: 10px; font-weight: 400;">Настройка Git</h2>\r\n<p style="margin-top: 15px; margin-bottom: 14px;">Перед началом работы нам необходимо немного настроить программу. Нам нужно установить имя пользователя и электронный адрес через командную строку:</p>\r\n<pre class=" hljs php" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git config --<span class="hljs-keyword" style="color: #b294bb;">global</span> user.name <span class="hljs-string" style="color: #b5bd68;">"Ваше имя"</span>\r\ngit config --<span class="hljs-keyword" style="color: #b294bb;">global</span> user.email <span class="hljs-string" style="color: #b5bd68;">"Ваш Email"</span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">Также нам нужно настроить параметры установок окончания строк, для Windows мы вводим две команды</p>\r\n<pre class=" hljs php" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git config --<span class="hljs-keyword" style="color: #b294bb;">global</span> core.autocrlf <span class="hljs-keyword" style="color: #b294bb;">true</span>\r\ngit config --<span class="hljs-keyword" style="color: #b294bb;">global</span> core.safecrlf <span class="hljs-keyword" style="color: #b294bb;">false</span>\r\ngit config --<span class="hljs-keyword" style="color: #b294bb;">global</span> core.eol native</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">На этом настройка заканчивается, можем начинать работу с проектом.</p>\r\n<h2 style="margin-top: 30px; margin-bottom: 10px; font-weight: 400;">Создание проекта</h2>\r\n<p style="margin-top: 15px; margin-bottom: 14px;">Допустим у нас есть папка с проектом, которую мы хотим разместить на GitHub.</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">1. Создаем репозиторий на сайте.</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;"><img src="http://rgblog.ru/site/rgblog/uploads/_pages/105/1.png" title="Создаем репозиторий на сайте" alt="Создаем репозиторий на сайте" /></p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">2. Инициализируем папку для Git репозитория. Это нужно сделать только один раз для каждого проекта.</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git init</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">3. Связываем папку с удаленным репозиторием</p>\r\n<pre class=" hljs javascript" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git remote add origin https:<span class="hljs-comment" style="color: #969896;">//github.com/LeoXCoder/test_project.git</span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">4. Добавляем все новые и измененные файлы</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git add .</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">5. Помечаем все новые и измененные файлы сообщением (<span style="font-weight: bold;">commit</span>)</p>\r\n<pre class=" hljs sql" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git <span class="hljs-operator"><span class="hljs-keyword" style="color: #b294bb;">commit</span> -m <span class="hljs-string" style="color: #b5bd68;">"message"</span></span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">- вместо message вписываем сообщение, например&nbsp;<i>Initial Commit.</i>&nbsp;или&nbsp;<i>Bugfix.</i></p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">6. Закачиваем код на удаленный репозиторий</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git push -u origin master</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">в таком виде используем только первый раз, потом используем команду без флагов</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git push</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;"><img src="http://rgblog.ru/site/rgblog/uploads/_pages/105/2.png" title="Создание проекта github" alt="Создание проекта github" /></p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;"><img src="http://rgblog.ru/site/rgblog/uploads/_pages/105/3.png" title="Закачиваем проект github" alt="Закачиваем проект github" /></p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">7. Можно посмотреть статус изменений, которые были сделаны.</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git status</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">8. Для скачивания репозитория используется команда</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git pull</pre>\r\n<h2 style="margin-top: 30px; margin-bottom: 10px; font-weight: 400;">Второй компьютер</h2>\r\n<p style="margin-top: 15px; margin-bottom: 14px;">Для использования репозитория на другом компьютере, используем следующие команды.</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">1. Клонирование репозитория</p>\r\n<pre class=" hljs php" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git <span class="hljs-keyword" style="color: #b294bb;">clone</span> https:<span class="hljs-comment" style="color: #969896;">//github.com/LeoXCoder/test_project.git</span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">В результате git скачает удаленный репозиторий в новую папку test-project</p>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">2. После каких-то изменений в коде, выполняем все те же команды</p>\r\n<pre class=" hljs sql" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git add .\r\ngit <span class="hljs-operator"><span class="hljs-keyword" style="color: #b294bb;">commit</span> -m <span class="hljs-string" style="color: #b5bd68;">"I changed the user module."</span>\r\ngit push</span></pre>\r\n<h2 style="margin-top: 30px; margin-bottom: 10px; font-weight: 400;">Откат изменений</h2>\r\n<p style="margin-top: 15px; margin-bottom: 14px;">1. Полный откат до предыдущего коммита</p>\r\n<pre class=" hljs sql" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git <span class="hljs-operator"><span class="hljs-keyword" style="color: #b294bb;">reset</span> HEAD <span class="hljs-comment" style="color: #969896;">--hard</span></span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">2. Сброс изменений в файле на версию коммита</p>\r\n<pre class=" hljs xml" style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git checkout <span class="hljs-tag" style="color: #cc6666;">&lt;<span class="hljs-title">filename</span>&gt;</span></pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">3. Откат до установленного тега, например v1</p>\r\n<pre class=" hljs " style="overflow: auto; font-family: ''PT Mono'', Monaco, Menlo, Consolas, ''Courier New'', monospace; font-size: 1em; margin: 10px 0px 14px; padding: 0.5em; background: #1d1f21; color: #c5c8c6;">git checkout v1</pre>\r\n<p style="margin-top: 10px; margin-bottom: 14px;">Для более лучшего пониманию лучше ознакомиться с&nbsp;<a href="http://githowto.com/ru" target="_blank" style="background-color: transparent; color: #556677;">интерактивным туром по Git</a></p>\r\n</div>', '', 1, 15, '2017-10-25 22:25:05', 38, '', '2017-10-25 22:26:24', 38, 0, '0000-00-00 00:00:00', '2017-10-25 22:25:05', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"article_layout":"","show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","info_block_show_title":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_associations":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_page_title":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 0, '', '', 1, 1, '{"robots":"","author":"","rights":"","xreference":""}', 1, '*', '');

-- --------------------------------------------------------

--
-- Структура таблицы `qhk3j_contentitem_tag_map`
--

CREATE TABLE IF NOT EXISTS `qhk3j_contentitem_tag_map` (
  `type_alias` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `core_content_id` int(10) unsigned NOT NULL COMMENT 'PK from the core content table',
  `content_item_id` int(11) NOT NULL COMMENT 'PK from the content type table',
  `tag_id` int(10) unsigned NOT NULL COMMENT 'PK from the tag table',
  `tag_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Date of most recent save for this tag-item',
  `type_id` mediumint(8) NOT NULL COMMENT 'PK from the content_type table'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Maps items from content tables to tags';

-- --------------------------------------------------------

--
-- Структура таблицы `qhk3j_content_frontpage`
